[86150] = { "Вартовий древніх королів", "Викликає вартового древніх королів, що [захищатиме вас протягом {1} секунд#to protect you for {1} sec][атакуватиме вашу поточну ціль протягом {1} секунд#attack your current target for {1} sec].\n\n[Поки вартовий активний, ваші наступні 5 цілющих заклять прямої дії зцілять цілі ще стільки ж здоров'я, а союзникам в межах {1} м від цілі зцілять {2}% від ефекту зцілення.#targets within {1} yards of the target for {2}% of the][Поки вартовий існує, ви отримуєте на {1}% менше шкоди.#all incoming damage is reduced by {1}%][Атаки вартового наповнюють вас древньою силою, що збільшує вашу силу на {1}% за кожен ефект.#increasing your Strength by {1}% per application][\n\nПісля зникнення вартового ви вивільните древню лють, що завдасть {1} шкоди і додатково {2} шкоди та кожен ефект древньої сили, поділеної між всіма цілями в межах {3} метрів.#causing {1} damage plus {2} per application of Ancient Power, divided among all targets within {3} y]", en="Guardian of Ancient Kings" },
-- Hallowed Ground
[84631] = { "Освячена земля", "Збільшує шкоду вашого \"Освячення\" на {1}% і зменшує його витрати мани на {2}%.#by {1}% and#cost by {2}%", en="Hallowed Ground" },
[84633] = { ref=84631 }, -- Hallowed Ground
-- Hammer of Justice
[853] = { "Молот правосуддя", "Приголомшує ціль на {1} с.#for {1} sec", "Приголомшено.", en="Hammer of Justice" },
-- Hammer of Wrath
[24275] = { "Молот гніву", "Кидає у ворога молот, що завдає [{1}#for {1} Holy][від {1} до {2}#for {1} to {2} Holy] шкоди святістю. Можна застосовувати лише проти ворогів, у яких не більше {1}% здоров'я.#have {1}%", en="Hammer of Wrath" },
-- Hammer of the Righteous
[53595] = { "Молот праведника", "Удар молотом по цілі, що завдає {1}% шкоди зброї та викликає хвилю світла, що вражає всі цілі в межах {2} м і завдає [{1}#for {1} Holy][від {1} до {2}#for {1} to {2} Holy] шкоди святістю. Створює заряд святої сили.#for {1}% weapon#within {2} y", en="Hammer of the Righteous" },
-- Hand of Protection
[1022] = { "Десниця захисту", "Захищає учасника групи чи рейду від усіх фізичних атак протягом {1} секунд, але протягом цього часу ціль не може атакувати та використовувати фізичні здібності. Гравці можуть мати лише одну десницю від кожного паладина одночасно. Не можна використовувати на цілях зі \"Стриманістю\". Спричиняє ефект \"Стриманість\" на {2} хв.#attacks for {1} sec#Forbearance for {2} min", "Невразливість до фізичних атак. Не може атакувати та використовувати фізичні здібності.", en="Hand of Protection" },
-- Hand of Reckoning
[62124] = { "Десниця відплати", "Провокує ціль атакувати вас, але не діє, якщо ціль вже атакує вас.", "Спровоковано.", en="Hand of Reckoning" },
-- Hand of Salvation
[1038] = { "Десниця спасіння", "Покладає десницю на учасника групи чи рейду, [зменшуючи всю його загрозу на {1}% кожні {2} с протягом {3} с#their total threat by {1}% every {2} sec for {3} sec][знімаючи всю загрозу з цілі протягом {1} с#all threat from the target for {1} sec]. Гравці можуть мати лише одну десницю від кожного паладина одночасно.", "[Загрозу тимчасово зменшено.#Threat temporarily reduced][Зменшує загрозу на {1}% щосекунди.#threat by {1}% each]", en="Hand of Salvation" },
-- Holy Light
[635] = { "Священне світло", "Зцілює дружній цілі від {1} до {2} здоров'я.#for {1} to {2}.", en="Holy Light" },
-- Holy Radiance
[82327] = { "Святе сяйво", "Наповнює дружню ціль енергією світла, що зцілює [{1}#yards for {1} and][від {1} до {2}#yards for {1} to {2} and] здоров'я і додатково {2} здоров'я цілі та союзникам в межах {1} м кожні {3} с протягом {4} с[ та надає заряд святої сили#grants a charge of Holy Power]. Ефективність зцілення зменшується для кожної цілі після шостої.#within {1} yards#another {2} every {3} sec for {4} sec", "Зцлення цілей в межах {1} м.#within {1} y", en="Holy Radiance" },
-- Holy Shield
[20925] = { "Священний щит", "Збільшує обсяг шкоди, що блокується щитом, на {1}% протягом {2} с.#additional {1}% for {2} sec", "Збільшує обсяг шкоди, що блокується щитом, на {1}%.#by {1}%", en="Holy Shield" },
-- Holy Shock
[20473] = { "Священний шок", "Вражає ціль святою енергією, завдаючи ворогу [{1}#causing {1} Holy][від {1} до {2}#causing {1} to {2} Holy] шкоди святістю або зцілюючи союзнику [{1}#or {1} healing][від {1} до {2}#or {1} to {2} healing] здоров'я, та створює заряд святої сили.", en="Holy Shock" },
-- Holy Wrath
[2812] = { "Священний гнів", "Посилає заряди святої сили в усі боки, завдаючи {1} шкоди, поділеної між всіма цілями в межах {2} м та приголомшуючи всіх демонів[, драконів, елементалів#Dragonkin, Elementals] та невмерлих на {3} с.#causing {1} Holy#within {2} y#for {3} sec", "Приголомшено.", en="Holy Wrath" },
-- Illuminated Healing
[76669] = { "Осяйне зцілення", "Ваші закляття прямого зцілення захищають ціль щитом, що поглинає {1}% від обсягу зцілення протягом {2} с. Кожна одиниця майстерності збільшує обсяг поглинання на {3}%.#for {1}% of the amount healed lasting {2} sec#by an additional {3}%", en="Illuminated Healing" },
[86273] = { "Осяйне зцілення", "Ваші цілющі закляття захищають ціль щитом, що поглинає відсоток від обсягу зцілення. Щит триває {1} с, а обсяг поглинання збільшується від показника майстерності.#lasts {1} sec", "Поглинає {1} шкоди.#Absorbs {1} damage.", en="Illuminated Healing" },
-- Improved Judgement
[87174] = { "Покращений присуд", "Збільшує дальність вашого \"Присуду\" на {1} м.#by {1} y", en="Improved Judgement" },
[87175] = { ref=87174 }, -- Improved Judgement
-- Infusion of Light
[53569] = { "Вливання Cвітла", "Збільшує ймовірність критичного ефекту вашого \"Священного шоку\" на {1}%. Окрім того, критичні ефекти \"Священного шоку\" зменшує час вимови наступного \"Зблиску світла\", \"Священного світла\", \"Божественного світла\" або \"Святого сяйва\" на {2} с.#Shock by {1}%#Radiance by {2} sec", en="Infusion of Light" },
[53576] = { ref=53569 }, -- Infusion of Light
[53672] = { "Вливання Cвітла", "Критичні удари \"Священного шоку\" зменшують час вимови наступного \"Зблиску світла\", \"Священного світла\", \"Божественного світла\" або \"Святого сяйва\" на {1} с.#Radiance by {1} sec", "Час вимови наступного \"Зблиску світла\", \"Священного світла\", \"Божественного світла\" або \"Святого сяйва\" зменшено на {1} с.#by {1} sec", en="Infusion of Light" },
