[20234] = { "Останнє слово", "Ваше \"Уславлення\" має на {1}% більшу ймовірність критичного ефекту при застосуванні до цілей, що мають не більше {2}% здоров'я.#a {1}% increased#with {2}% or", en="Last Word" },
[20235] = { ref=20234 }, -- Last Word
