-- Seal of Corruption
[348704] = { "Печатка осквернення", "Наповнює паладина святою силою, внаслідок чого атаки ближнього бою, накладають ефект осквернення крові, що завдає додатково {1} шкоди святістю протягом {2} с. Осквернення крові може накопичуватись до {3} разів. Атаки паладина також завдаватимуть додатково до {4}% шкоди від зброї у вигляді шкоди святістю в залежності від кількості ефектів. Лише одна печатка може бути задіяна на паладинові одночасно. Триває {5} хв.\n\nВивільнення енергії цієї печатки завдасть {6} шкоди святістю, що збільшується на {7}% за кожен ефект осквернення крові на цілі.#deals {1} additional Holy damage over {2} sec#up to {3} times#deals up to {4}% weapon#Lasts {5} min#will deal {6} Holy#increased by {7}% for each", "Атаки ближнього бою завдають додаткової шкоди святістю.", en="Seal of Corruption" },
-- Seal of Insight
[20165] = { "Печатка прозріння", "Наповнює паладина божественною силою на {1} хв, внаслідок чого атаки ближнього бою, що вражають одну ціль, можуть зцілити паладину {2} здоров'я та відновити {3}% мани. Лише одна печатка може бути задіяна на паладинові одночасно.\n\nВивільнення енергії цієї печатки завдасть ворогу {4} шкоди святістю.#for {1} min#for {2} and restore {3}% of#deal {4} Holy", "Атаки ближнього бою можуть зцілити та відновити ману.", en="Seal of Insight" },
-- Seal of Justice
[20164] = { "Печатка правосуддя", "Наповнює паладина духом правосуддя на {1} хв, внаслідок чого атаки ближнього бою, що вражають одну ціль, обмежують максимальну швидкість руху цілі на {2} с та додатково завдають {3} шкоди святістю. Лише одна печатка може бути задіяна на паладинові одночасно.[\n\nВивільнення енергії цієї печатки завдасть ворогу {1} шкоди святістю.#Unleashing this Seal's energy will deal {1} Holy damage]#justice for {1} min#speed for {2} sec and deal {3} additional Holy", "Атаки ближнього бою обмежують максимальну швидкість руху цілі на {1} с та додатково завдають {2} шкоди святістю.#for {1} sec and deal {2} additional", en="Seal of Justice" },
[20170] = { "Печатка правосуддя", "Наповнює паладина духом правосуддя на {1} хв, внаслідок чого атаки ближнього бою, що вражають одну ціль, обмежують максимальну швидкість руху цілі на {2} с та завдають додаткової шкоди святістю. Лише одна печатка може бути задіяна на паладинові одночасно.\n\nВивільнення енергії цієї печатки завдасть ворогу {3} шкоди святістю.#justice for {1} min#speed for {2} sec#will deal {3} Holy damage", "Швидкість руху не можу бути більшою за звичайну.", en="Seal of Justice" },
-- Seal of Righteousness
[20154] = { "Печатка праведності", "Наповнює паладина святим духом на {1} хв, надаючи кожній атаці ближнього бою {2} додаткової шкоди святістю.[ Також збільшує вправність на {1}.#grants 10 expertise] Лише одна печатка може бути задіяна на паладинові одночасно.[\n\nВивільнення енергії цієї печатки завдасть ворогу {1} шкоди святістю.#Unleashing this Seal's energy will cause {1} Holy damage]#spirit for {1} min#attack {2} additional Holy", "Атаки ближнього бою завдають додатково {1} шкоди святістю.#additional {1} Holy", en="Seal of Righteousness" },
-- Seal of Truth
[31801] = { "Печатка правди", "Наповнює паладина святою силою, внаслідок чого атаки ближнього бою, що вражають одну ціль, осуджують її, завдаючи додатково {1} шкоди святістю протягом {2} с. Ефект осуду може накопичуватись до {3} разів. Коли ціль матиме {4} ефектів осуду, атаки паладина також завдаватимуть {5}% шкоди від зброї у вигляді шкоди святістю.[ Також збільшує вправність на {1}.#grants {1} expertise] Лише одна печатка може бути задіяна на паладинові одночасно. Триває {6} хв.[\n\nВивільнення енергії цієї печатки завдасть {1} шкоди святістю, що збільшується на {2}% за кожен ефект осуду на цілі.#Seal's energy will deal {1} Holy damage to an enemy, increased by {2}% for each application]#deals {1} additional Holy damage over {2} sec#up to {3} times#stacked to {4} times#deals {5}% weapon#Lasts {6} min", "Атаки по одній цілі завдають додаткової шкоди святістю протягом {1} с.#over {1} sec", en="Seal of Truth" },
-- Seals of Command
[85126] = { "Командирські печатки", "Ефекти ваших печаток праведності, правди та правосуддя також завдають {1}% шкоди від зброї. Окрім того, ваша печатка праведності тепер завдає шкоди всім ворогам на дистанції ближнього бою.#deal {1}% weapon", en="Seals of Command" },
-- Seals of the Pure
[20224] = { "Печатки чистоти", "Збільшує шкоду від печаток праведності, правди та правосуддя на {1}%.#by {1}%", en="Seals of the Pure" },
[20225] = { ref=20224 }, -- Seals of the Pure
-- Selfless Healer
[85803] = { "Самовідданий цілитель", "Знижує час відновлення здібності \"Уславлення\" на {1} с. Крім того, використання цієї здібності для зцілення інших збільшує ефект зцілення на {2}%, а також на {3} с збільшує вашу шкоду на {4}% за кожен витрачений заряд святої сили.#Glory by {1} sec#heal by {2}% and#done by {3}% per#Power for {4} sec", en="Selfless Healer" },
