[28176] = { "Обладунок скверни", "Оточує заклинача енергією скверни, що збільшує силу заклять на {1} і зцілює на {2}% від шкоди, завданої закляттями, що діють на одну ціль.\n\nОдночасно на чорнокнижника може діяти лише один обладунок.#power by {1} and#healed for {2}% of", "Збільшує силу заклять на {1} і зцілює на {2}% від шкоди, завданої закляттями, що діють на одну ціль.#power by {1} and#healed for {2}%" }, -- Fel Armor
-- Fel Flame
[77799] = { "Полум'я скверни", "Завдає від {1} до {2} шкоди від тіньового полум'я ворожій цілі та збільшує тривалість ефекту \"Жертовний вогонь\" чи \"Нестабільна химородь\" на {3} с.#Deals {1} to {2} Shadowflame#by {3} sec" }, -- Fel Flame
-- Hand of Gul'dan
[71521] = { "Рука Ґул'дана", "Викликає метеорит, що падає на ціль, завдаючи від {1} до {2} шкоди від тіньового полум'я і випромінює магічну ауру в радіусі {3} м, що збільшує ймовірність отримання критичного удару від демонів чорнокнижника на {4}%. Аура триває {5} с.#dealing {1} to {2} Shadowflame#within {3} y#a {4}% increased#for {5} sec" }, -- Hand of Gul'dan
-- Haunt
[48181] = { "Мара", "Випустити на ворога примару, яка завдасть {1} шкоди від тіні, а також збільшить вашу періодичну шкоду від тіні по ньому на {2}% протягом {3} с. Коли \"Мара\" закінчиться або розвіється, ви зцілитеся на {4}% від шкоди, якої вона завдала цілі.#dealing {1} Shadow#target by {2}% for {3} sec#you for {4}% of", "Отриману періодичну шкоду від тіні збільшено на {1}%.#by {1}%" }, -- Haunt
-- Health Funnel
