[191267] = { "руна телепортації: Передпокій", use="Переміщає вас до передпокою Наксрамаса." }, -- Rune of Teleportation: Antechamber
[191268] = { "сфера осквернення", use="Розтрощити кулю, щоб збільшити кількість малих смертекаменів у поплічників Кел'Тузада на 50% протягом 30 хв." }, -- Orb of Corruption
[191272] = { "жезл некромантії", use="Підкоряє волю невмерлої цілі в Наксрамасі, причаровуючи її на 5 хв." }, -- Rod of Necromancy
[191280] = { "скупчення грибних спор", use="Ваші бойові здібності можуть викликати спору, що після смерті накладає на гравців неподалік \"Грибоцвіт\". Ефект триває 1 годину.\n\nГрибоцвіт \nЗавдає 150 шкоди від природи щосекунди, натомість збільшуючи ймовірність критичного удару в ближньому та дальньому бою та критичного ефекту заклять на 15%." }, -- Fungal Spore Cluster
[191288] = { "руна телепортації: Лігво крижаного змія", use="Переміщає вас до лігва крижаного змія в Наксрамасі." }, -- Rune of Teleportation: Frostwyrm's Lair
[191292] = { "ритуальний кинджал", use="Поглинає життєву силу навколишніх ворогів, збільшуючи витривалість на 3 за кожну уражену ціль. Ефект накопичується до 40 разів і триває 1 годину." }, -- Ritual Dagger
[191312] = { "захисна філактерія", equip="Захищає від атаки, яка мала б вас убити. Цей ефект також надає вам невразливість до шкоди на 3 секунди і знищує філактерію." }, -- Failsafe Phylactery
[191313] = { "маска смерті", use="Прийняти образ мешканця Наксрамаса." }, -- Mask of Death
[191414] = { "кістяний артефакт", use="Захоплює душу мертвої цілі, надаючи вам сприятливий ефект. Ефект триває 1 годину." }, -- Skeletal Artifact
[191458] = { "знищена філактерія", flavor="Більше не принесе жодної користі." }, -- Shattered Phylactery
[191459] = { "руна удачі", use="Посилює Кел'Тузада та його лейтенантів некромантичною енергією, збільшуючи їхній запас здоров'я та шкоду. Посилені вороги принесуть додаткову здобич.\n\nЦей ефект накопичується і триває цілий тиждень." }, -- Rune of Fortune
