[92364] = { "Дар Малфуріона", "Час відновлення \"Спокою\" зменшується до {1} хв, а час відновлення \"Дубової шкіри\" зменшується на {2} с.#to {1} min#by {2} sec", en="Malfurion's Gift" },
-- Mangle
[33876] = { "Нівечення", "Нівечить ціль, завдаючи {1}% від звичайної шкоди плюс {2}[ та зменшуючи швидкість руху цілі на {1}% протягом {2} с#movement speed by {1}% for {2} sec]. Збільшує довжину комбінації на {3} прийом.#for {1}% normal damage plus {2}#{3} combo", en="Mangle" },
[33878] = { "Нівечення", "Нівечить ціль, завдаючи {1}% шкоди зброї[ та зменшуючи швидкість руху цілі на {1}% протягом {2} с#movement speed by {1}% for {2} sec]. Надає {2} люті.#for {1}% weapon#generate {2} Rage", en="Mangle" },
[33917] = { "Нівечення", "Нівечить ціль, завдаючи фізичної шкоди[ та зменшуючи швидкість руху цілі на {1}% протягом {2} с#movement speed by {1}% for {2} sec]. Шкода залежить від подоби. Надає {1} люті у ведмежій подобі та збільшує довжину комбінації на {2} прийом в котячій подобі.#Generates {1} Rage#awards {2} combo", en="Mangle" },
-- Mark of the Wild
[1126] = { "Знак дикої природи", "Збільшує силу, спритність та інтелект дружньої цілі на {1}% протягом {2} год.\n\nЯкщо ціль перебуває у вашій групі або рейді, то ефект отримають всі учасники групи чи рейду.#Intellect by {1}% for {2} h", "Силу, спритність та інтелект збільшено на {1}%.#by {1}%", en="Mark of the Wild" },
-- Mass Entanglement
[102359] = { "Масове обплетення", "Приковує ціль на місці протягом {1} с та поширюється на навколишні цілі. Подальша завдана шкода може перервати дію ефекту. Можна використовувати в будь-якій подобі.#for {1} sec", "Приковано.", en="Mass Entanglement" },
-- Maul
[6807] = { "Терзання", "Терзає ціль, завдаючи {1}% шкоди зброї[ та додатково {1}% шкоди цілі неподалік#an additional {1}% weapon damage to a nearby target].[ Зменшує швидкість руху цілі на {1}% протягом {2} с.#movement speed by {1}% for {2} sec]\n\nЗавдає на {2}% більше шкоди цілям з кровотечею.\n\nВикористовуйте для витрати надлишкової люті.#target for {1}% weapon damage#Deals {2}% additional damage", en="Maul" },
-- Mighty Bash
[5211] = { "Могутній стусан", "Пробуджує дух Урсока, що приголомшує ціль на {1} с. Можна використовувати в будь-якій подобі.#for {1} sec", "Приголомшено.", en="Mighty Bash" },
-- Moonfire
[8921] = { "Місячний вогонь", "Обпалює ворога, завдаючи [{1}#for {1} Arcane][від {1} до {2}#for {1} to {2} Arcane] шкоди арканою та додатково {1} шкоди арканою протягом {2} с.[ Критичні удари \"Зоряного вогню\" та \"Зоряного сплеску\" продовжують тривалість \"Місячного вогню\" на {1} с.#Moonfire's duration by {1} sec]#additional {1}#over {2} sec", "{1} шкоди від аркани кожні {2} с.#{1} Arcane damage every {2} sec", en="Moonfire" },
-- Moonkin Aura
[24907] = { "Аура місячного совуха", "В подобі місячного совуха ви збільшуєте швидкість заклять всіх учасників групи та рейду в межах {1} м на {2}%.#within {1} y#by {2}%", "Збільшує швидкість заклять на {1}%.#by {1}", en="Moonkin Aura" },
-- Naturalist
[17073] = { "Натураліст", "Збільшує все ваше зцілення на {1}%.#by {1}%", en="Naturalist" },
-- Nature's Cure
[88423] = { "Природне зцілення", "Позбавляє ціль шкідливих ефектів, знімаючи всі ефекти магії, проклять та отрут.", en="Nature's Cure" },
-- Nature's Focus
[84736] = { "Природне зосередження", "Збільшує ймовірність завдання удару закляттями \"Місячний вогонь\" та \"Гнів\" на {1}%.\n\nЗменшує затримку вимови заклять \"Цілющий дотик\", \"Розростання\", \"Спокій\", \"Переродження\", \"Опутуюче коріння\", \"Циклон\" та \"Підживлення\" при отриманні ударів на {2}%.#Wrath by {1}%#attacks by {2}% while", en="Nature's Focus" },
-- Nature's Grace
[16886] = { "Природна благодать", "Ваша швидкість заклять збільшується на {1}% після отримання ефекту затемнення.#gain {1}%", "Швидкість вимови заклять збільшено на {1}%.#by {1}%", en="Nature's Grace" },
-- Nature's Grasp
[16689] = { "Схоплення природи", "Під час дії закляття кожен раз, коли ворог завдає удару заклиначеві, його вразить закляття \"Опутуюче коріння\". {1} заряд. Триває {2} с.#{1} charge#Lasts {2} sec", "При отриманні шкоди в ближньому бою обплутає ворога.", en="Nature's Grasp" },
-- Nourish
[50464] = { "Підживлення", "Зцілює дружній цілі [{1}#friendly target for {1}][від {1} до {2}#friendly target for {1} to {2}] здоров'я.\n\nЗцілює на {1}% більше, якщо на ціль діє \"Омолодження\", \"Розростання\", \"Життєцвіт\" або \"Дикий ріст\".#additional {1}%", en="Nourish" },
-- Nurturing Instinct
[33873] = { "Інстинкт піклування", "Збільшує силу заклять природи на {1}% від вашої спритності.#by {1}% of", en="Nurturing Instinct" },
-- Omen of Clarity
[16864] = { "Передвістя ясності", "Ваші автоатаки можуть наділити вас ефектом \"Ясномови\".\n\n\"Ясномова\" знижує витрати мани, енергії або люті на наступне ваше немиттєве атакуюче чи цілюще закляття або атакуючу звірину здібність на {1}%.#by {1}%", "Автоатаки можуть спричинити ефект \"Ясномови\"", en="Omen of Clarity" },
-- Owlkin Frenzy
[48391] = { "Совине божевілля", "Атаки, отримані вами в подобі місячного совуха, з ймовірністю {1}% викличуть божевілля, що збільшить вашу шкоду на {2}% та зробить вас невразливими до затримки вимови заклять спеціалізації \"Баланс\". Ефект триває {3} с.#a {1}% chance#damage by {2}%#Lasts {3} sec", "Шкоду збільшено на {1}%. Невразливість до затримки вимови заклять спеціалізації \"Баланс\".#by {1}%", en="Owlkin Frenzy" },
[48393] = { "Совине божевілля", "Атаки, отримані вами в подобі місячного совуха, з ймовірністю {1}% викличуть божевілля, що збільшить вашу шкоду на {2}% та зробить вас невразливими до затримки вимови заклять спеціалізації \"Баланс\". Ефект триває {3} с.#a {1}% chance#damage by {2}%#Lasts {3} sec", en="Owlkin Frenzy" },
-- Predatory Swiftness
[16974] = { "Удари хижака", "Ваші завершальні рухи з ймовірністю {1}% за кожен прийом в комбінації дозволять вимовити наступне закляття \"Опутуюче коріння\", \"Цілющий дотик\", \"Сплячка\" чи \"Переродження\" миттєво, без витрат мани та в будь-якій подобі.#a {1}% chance", en="Predatory Swiftness" },
-- Primal Fury
[16953] = { "Первісна лють", "Ваші критичні удари здібностями котячої подоби, що збільшують довжину комбінації, збільшують довжину комбінації на ще один прийом.", en="Primal Fury" },
[16961] = { "Первісна лють", "Ви отримуєте {1} люті щоразу, коли ви завдаєте критичного удару автоатакою або \"Нівеченням\", а також збільшуєте довжину комбінації на один прийом щоразу, коли завдаєте критичного удару здібностями котячої подоби, що збільшують довжину комбінації та вражають одну ціль.#additional {1} Rage", en="Primal Fury" },
-- Prowl
[5215] = { "Зачаїтися", "Дозволяє друїду пересуватися непомітно, але зменшує швидкість руху на {1}%. Триває до скасування. Використання цієї здібності активує котячу подобу.#speed by {1}%", "Непомітність.[ Рух сповільнено на {1}%.#slowed by {1}%]", en="Prowl" },
-- Rake
[24331] = { "Розрив", "Рве ціль, завдаючи фізичної шкоди та додаткової шкоди протягом {1} с.#over {1} sec", "Шкода від кровотечі кожні {1} с.#every {1} sec", en="Rake" },
[24332] = { "Розрив", "Рве ціль, завдаючи від {1} до {2} шкоди та додатково {3} шкоди протягом {4} с.#for {1} to {2} damage#additional {3} damage over {4} sec", "{1} шкоди від кровотечі кожні {2} с.#for {1} damage every {2} sec", en="Rake" },
