-- Ravage
[6785] = { "Знищення", "Нищить ціль, завдаючи їй {1}% шкоди плюс {2}[ та зменшуючи її швидкість руху на {1}% протягом {2} с#movement speed by {1}% for {2} sec]. Друїд має зачаїтися і перебувати позаду цілі. Збільшує довжину комбінації на {3} прийом.\n\nЙмовірність завдання критичного удару збільшується на {4}% проти цілей, що мають більше {5}% здоров'я.#causing {1}%#plus {2}#{3} combo#has a {4}% increased chance to critically strike targets with over {5}% health", en="Ravage" },
-- Regrowth
[8936] = { "Розростання", "Зцілює дружній цілі [{1}#for {1} and][від {1} до {2}#for {1} to {2} and] здоров'я і додатково {1} здоров'я протягом {2} c.\n\nЙмовірність критичного удару \"Розростання\" збільшено на {3}%.\n\nЕфект \"Розростання\" автоматично поновлюється до {4} секунд щоразу, коли зцілює ціль, що має менше {5}% здоров'я.[\n\n|cFF33AA11Дерево життя: Вимовляється миттєво.|r#Tree of Life: Instant cast]#another {1} over {2} sec#has a {3}% increased chance#refreshes to {4} sec each time#or below {5}% health", "Зцілення {1} здоров'я кожні {2} с.#{1} every {2} sec", en="Regrowth" },
-- Rejuvenation
[774] = { "Омолодження", "Зцілює цілі {1} здоров'я та додатково {2} кожні {3} с протягом {4} с.#for {1} and an additional {2} every {3} sec for {4} sec", "Зцілює {1} здоров'я кожні {2} с.#{1} damage every {2} sec", en="Rejuvenation" },
-- Remove Corruption
[2782] = { "Зняття пошесті", "Нейтралізує ефекти пошесті на дружній цілі, знімаючи всі ефекти проклятя та отрути.", en="Remove Corruption" },
-- Revitalize
[48544] = { "Відродження", "Періодичне зцілення заклять \"Омолодження\" та \"Життєцвіт\" з ймовірністю {1}% відновлюють вам {2}% мани. Цей ефект не може спрацьовувати частіше, ніж раз на {3} с.#a {1}% chance#regenerate {2}%#every {3} sec", en="Revitalize" },
-- Rip
[1079] = { "Розірвання", "Завершальний рух, що завдає шкоди з часом. Шкода збільшується залежно від довжини комбінації:\n  1 прийом: {1} шкоди за {2} cекунд\n  2 прийоми: {3} шкоди за {4} cекунд\n  3 прийоми: {5} шкоди за {6} cекунд\n  4 прийоми: {7} шкоди за {8} cекунд\n  5 прийомів: {9} шкоди за {10} cекунд\n\nЩоразу, коли ви вражаєте ціль \"Шматуванням\", \"Знищенням\" чи \"Нівеченням\" в котячій подобі, тривалість ефекту \"Розірвання\" на цілі подовжується на {11} с, максимум до {12} с.#1 point : {1} damage over {2}#2 points: {3} damage over {4}#3 points: {5} damage over {6}#4 points: {7} damage over {8}#5 points: {9} damage over {10}#extended by {11} sec#up to a maximum of {12} sec", "{1} шкоди від кровотечі кожні {2} с.#for {1} damage every {2} sec", en="Rip" },
-- Savage Roar
[52610] = { "Дикий рев", "Завершальний рух, що збільшує фізичну шкоду на {1}%. Можна використовувати лише в котячій подобі. Тривалість ефекту залежить від довжини комбінації:\n  1 прийом:   {2} с\n  2 прийоми: {3} с\n  3 прийоми: {4} с\n  4 прийоми: {5} с\n  5 прийомів: {6} с#by {1}%#1 point  : {2} sec#2 points: {3} sec#3 points: {4} sec#4 points: {5} sec#5 points: {6} sec", "Фізичну шкоду збільшено на {1}%.#by {1}%", en="Savage Roar" },
-- Shooting Stars
[93399] = { "Падаючі зірки", "З ймовірністю {1}% після завдання критичної періодичної шкоди закляттями \"Місячний вогонь\" або \"Сонячний вогонь\" ваш \"Зоряний сплеск\" миттєво відновиться, та протягом {2} с його можна буде вимовити миттєво. Ймовірність зменшується в PvP-бою з кількома цілями.#a {1}% chance#within {2} sec", en="Shooting Stars" },
-- Shred
[5221] = { "Шматування", "Шматує ціль, завдаючи {1}% шкоди плюс {2}[ та зменшуючи швидкість руху цілі на {1}% протягом {2} с#movement speed by {1}% for {2} sec]. Необхідно бути позаду цілі. Збільшує довжину комбінації на {3} прийом.\n\nЗавдає на {4}% більше шкоди цілям з кровотечею.#causing {1}%#plus {2} to#Awards {3} combo#Deals {4}% additional", en="Shred" },
-- Skull Bash
[80964] = { "Лобова атака", "Ви робите ривок та б'єте ціль по черепу, перериваючи вимову заклинань та запобігаючи застосуванню заклять тієї ж школи протягом {1} секунд.\n\nЗбільшує витрати мани цілі на закляття на {2}% протягом {3} с.#cast for {1} sec#by {2}% for {3} sec", "Перервано.", en="Skull Bash" },
[80965] = { ref=80964 }, -- Skull Bash
-- Solar Beam
[78675] = { "Сонячний промінь", "Ви викликаєте промінь сонячного світла на місце розташування ворожої цілі, який перериває вимову заклять та знемовлює всіх ворогів у області дії променя. Сонячний промінь існує {1} с.#for {1} sec", "Знемовлено.", en="Solar Beam" },
