-- Stampeding Roar
[77761] = { "Розполохуючий рев", "Друїд реве, збільшуючи швидкість руху всіх дружніх гравців в межах {1} м на {2}% протягом {3} секунд та знімаючи всі ефекти обмеження руху.#within {1} yards by {2}% for {3} sec", "Швидкість руху збільшено на {1}%.#by {1}%", en="Stampeding Roar" },
[77764] = { "Розполохуючий рев", "Друїд реве, збільшуючи швидкість руху всіх дружніх гравців в межах {1} м на {2}% протягом {3} секунд та знімаючи всі ефекти обмеження руху.\n\nНе перериває дію здібності \"Зачаїтися\".#within {1} yards by {2}% for {3} sec", "Швидкість руху збільшено на {1}%.#by {1}%", en="Stampeding Roar" },
-- Starfall
[48505] = { "Зорепад", "Ви викликаєте шквал зірок з неба на [уражені вашим \"Місячним вогнем\" чи \"Сонячний вогонь\" цілі#targets afflicted by your Moonfire][всі цілі#all targets] в межах {1} м[, з якими ви перебуваєте в бою#that you're in combat with]. Кожна зірка завдає [{1}#dealing {1} Arcane][від {1} до {2}#dealing {1} to {2} Arcane] арканічної шкоди. Викликає до {2} зірок. Триває {3} с. Місячне затемнення завершує відновлення зорепаду.\n\nЗміна подоби та верхова їзда скасовує ефект. Будь-який ефект, що призводить до втрати контролю над персонажем, призупиняє ефект зорепаду.#within {1} y#Maximum {2} stars#Lasts {3} sec", "Викликає зорі з неба.", en="Starfall" },
-- Starfire
[2912] = { "Зоряний вогонь", "Завдає цілі [{1}#Causes {1} Arcane][від {1} до {2}#Causes {1} to {2} Arcane] шкоди від аркани.[ Додає {1} сонячної енергії.#Generates {1} Solar]", en="Starfire" },
-- Stars
[62134] = { "Зорі", en="Stars" },
-- Starsurge
[78674] = { "Зоряний сплеск", "Ви об'єднуєте силу місяця і сонця, спрямовуючи на ціль руйнівний потік енергії. Завдає цілі [{1}#Causes {1} Spellstorm][від {1} до {2}#Causes {1} to {2} Spellstorm] шкоди від чарівної бурі та додає {1} місячної або сонячної енергії, залежно від того, яка з них вам потрібна.#generates {1} Lunar", en="Starsurge" },
-- Sunfire
[93402] = { "Сонячний вогонь", "Обпалює ворога, завдаючи {1} шкоди від природи та додатково {2} шкоди від природи протягом {3} с. Критичні удари \"Гніву\" та \"Зоряного сплеску\" продовжують тривалість \"Сонячного вогню\" на цілі на {4} с.#for {1} Nature#additional {2}#over {3} sec#duration by {4} sec", "{1} шкоди від природи кожні {2} с.#{1} Nature damage every {2} sec", en="Sunfire" },
-- Survival Instincts
[61336] = { "Інстинкти виживання", "Зменшує всю отриману шкоду на {1}% протягом {2} с.#by {1}% for {2} sec", "Отриману шкоду зменшено на {1}%.#by {1}%", en="Survival Instincts" },
-- Swift Flight Form
[40120] = { "Стрімка політна подоба", "Набути стрімкої політної подоби, що збільшує швидкість руху на {1}% та дозволяє літати. Не можна використовувати в бою. Заміняє \"Політну подобу\".\n\nЗміна подоби звільняє заклинача від ефектів обмеження руху.#speed by {1}%", "Невразливість до ефектів поліморфу. Швидкість руху збільшено на {1}%, дозволяє літати.#by {1}%", en="Swift Flight Form" },
-- Swiftmend
[18562] = { "Негайне відновлення", "Миттєво зцілює дружню ціль, що перебуває під ефектом \"Омолодження\" чи \"Розростання\", відновлюючи їй {1} здоров'я.\n\nОкрім того, відновлює {2} здоров'я кожні {4} с протягом {5} с трьом союзникам з найменшим запасом здоров'я в межах {3} м від цілі.#effect for {1}#restores {2} health#within {3} yards of the initial target every {4} sec for {5} sec", en="Swiftmend" },
-- Swipe
[779] = { "Розмах", "Розлого б'є ворогів поблизу, завдаючи {1} шкоди.\n\nЗавдає на {2}% більше шкоди цілям з кровотечею.#inflicting {1} damage#Deals {2}% increased", en="Swipe" },
[62078] = { "Розмах", "Розлого б'є ворогів поблизу, завдаючи {1}% шкоди від зброї.\n\nЗавдає на {2}% більше шкоди цілям з кровотечею.\n\nПродовжує комбінацію на один прийом якщо влучає по цілі.#inflicting {1}% weapon#Deals {2}% additional", en="Swipe" },
-- Teleport: Moonglade
[18960] = { "Телепортація: Місячна галявина", "Телепортує заклинача до Місячної галявини.\n\nПовторна вимова цього закляття на Місячній галявині перенесе заклинача неподалік місця відправлення.", en="Teleport: Moonglade" },
-- Thick Hide
[16931] = { "Товста шкура", "Збільшує бонус до броні від ведмежої подоби до {1}%, зменшує всю отриману магічну шкоду на {2}%, зменшує всю отриману фізичну шкоду на {3}% та зменшує шанс отримання критичного удару від атак на {4}%.\n\nТакож зменшує час відновлення \"Дубової шкіри\" на {5} с.#Bear Form to {1}%#magical damage taken by {2}%#physical damage taken by {3}%#critically hit by attacks by {4}%#Barkskin by {5} sec", en="Thick Hide" },
-- Thrash
[77758] = { "Трощити", "Вдаряє всіх ворогів в межах {1} м, завдаючи {2} шкоди від кровотечі та додатково {3} шкоди протягом {4} с, а також накладаючи ефект \"Ослаблені удари\".\n\nЩоразу, коли ця здібність завдає шкоди, з ймовірністю {5}% відновлення \"Нівечення\" завершиться.#within {1} yards#dealing {2} bleed damage and an additional {3} damage over {4} sec#has a {5}% chance", "{1} шкоди від кровотечі кожні {2} с.#for {1} damage every {2} sec", rune=115798, en="Thrash" },
-- Tiger's Fury
[5217] = { "Лють тигра", "Збільшує фізичну шкоду на {1}% протягом {2} с та миттєво відновлює {3} енергії. Не можна активовувати, поки діє \"Берсерк\". Можна використовувати лише в котячій подобі.#by {1}% for {2} sec#restores {3} Energy", "Збільшує фізичну шкоду на {1}%.#by {1}%", en="Tiger's Fury" },
-- Track Humanoids
[5225] = { "Вистежування гуманоїдів", "Відображає на мінімапі місцезнаходження гуманоїдів[ та звірів#and beasts] неподалік.", "Вистежування гуманоїдів.", en="Track Humanoids" },
-- Tranquility
[740] = { "Спокій", "Зцілює 5 (або 12 в підземеллях на 25 гравців) найближчих учасників групи або рейду з найменшим рівнем здоров'я в радіусі 40 м кожні 2 секунди протягом 8 секунд. \n\nСпокій зцілює {1} здоров'я і додатково {2} кожні 2 секунди протягом 8 секунд. Ефект накопичується до 3 разів. Друїд має підтримувати закляття.#for {1} plus an additional {2} every", "Зцілює найближчих учасників групи або рейду з найменшим рівнем здоров'я на {1} здоров'я і додатково {2} кожні 2 секунди протягом 8 секунд. #for {1} plus an additional {2} every", en="Tranquility" },
-- Ursol's Vortex
[102793] = { "Вихор Урсола", "Створює вихор вітру у вказаному місці, який знижує швидкість руху всіх ворогів у межах {1} м на {2}%.\n\nПід час першої спроби ворога покинути вихор вітри притягнуть його назад до центру.\n\nТриває {3} с. Можна використовувати в будь-якій подобі.#within {1} yards by {2}%#Lasts {3} sec", "Швидкість руху зменшено на {1}%, а вітри перешкоджають руху.#by {1}%", en="Ursol's Vortex" },
[102794] = { "Вихор Урсола", "Створює вихор вітру у вказаному місці, який притягує ворогів в межах {1} м.#within {1} y", en="Ursol's Vortex" },
[118283] = { ref=102794 }, -- Ursol's Vortex
[127797] = { ref=102793 }, -- Ursol's Vortex
-- Vengeance
[84840] = { "Помста", "Коли ви отримуєте шкоду, перебуваючи у ведмежій подобі, ви збільшуєте силу атаки на {1}% від отриманої шкоди (без врахування ефектів зменшення шкоди) протягом {2} с. Набуття кошачої подоби скасує дію цього ефекту.#gain {1}% of#for {2} sec", en="Vengeance" },
-- Wild Charge
[16979] = { "Дикий ривок", "Ривок у ворога, що знерухомлює його на {1} с.#for {1} sec", "Знерухомлено.", en="Wild Charge" },
[49376] = { "Дикий ривок", "Стрибок за спину ворога, що спричиняє запаморочення на {1} с.#for {1} sec", "Запаморочення.", en="Wild Charge" },
-- Wild Growth
[48438] = { "Дикий ріст", "Зцілює {1} учасникам групи чи рейду в радіусі {2} м від цілі {3} здоров'я протягом {4} секунд. Першочергово зцілює цілі з найменшим запасом здоров'я.\n\nЗцілення спочатку відбувається швидко, а ближче до кінця дії ефекту сповільнюється.[\n\n|cFF33AA11Дерево життя: Діє ще на {1} цілі.|r#Tree of Life: Affects an additional {1} targets]#up to {1} friendly#within {2} yards#for {3} over {4} sec", "Зцілює {1} здоров'я кожні {2} с.#{1} damage every {2}", en="Wild Growth" },
-- Wild Mushroom
[88747] = { "Дикий гриб", "Вирощує магічний гриб з {1} здоров'я у вказаному місці.\n\nЧерез {2} с гриб стане невидимим.\n\nІнші здібності групи \"Дикий гриб\" можна застосовувати на вирощені гриби.\n\nОдночасно можна розмістити до {3} грибів.#with {1} health#After {2} sec#Only {3} mushrooms", en="Wild Mushroom" },
-- Wild Mushroom: Detonate
[78777] = { "Підрив диких грибів", "Підірвати всі ваші дикі гриби, завдаючи [{1}#dealing {1} Nature][від {1} до {2}#dealing {1} to {2} Nature] шкоди від природи всім ворогам в межах {1} м та створюючи на їх місці грибний покрив, що сповільнює ворогів в межах {2} м на {3}% протягом {4} с.#all nearby targets within {1} y#area within {2} y#targets by {3}%#lasting {4} sec", en="Wild Mushroom: Detonate" },
[88751] = { ref=78777 }, -- Wild Mushroom: Detonate
-- Wrath
[5176] = { "Гнів", "Завдає цілі [{1}#Causes {1} Nature][від {1} до {2}#Causes {1} to {2} Nature] шкоди від природи.[ Додає {1} місячної енергії.#Generates {1} Lunar Energy]", en="Wrath" },

-- Forms
[1066] = { "Водна подоба", "Набути водної подоби, що збільшує швидкість плавання на {1}% та дозволяє дихати під водою. Також захищає заклинача від ефектів поліморфу.\n\nЗміна подоби звільняє заклинача від ефектів обмеження руху.#speed by {1}%", "Невразливість до ефектів поліморфу. Збільшує швидкість плавання на {1}% та дозволяє дихати під водою.#speed by {1}%", en="Aquatic Form" },
[768] = { "Котяча подоба", "Набути котячої подоби, що збільшує силу атаки в залежності від показника спритності, збільшу шкоду автоатак на {1}% та збільшує швидкість руху на {2}% (поєднується з більшістю інших ефектів збільшення швидкості руху).[ Окрім того, все отримане зцілення збільшується на {1}%.#healing done to you is increased by {1}%]\n\nТакож захищає заклинача від ефектів поліморфу та дозволяє використання котячих здібностей.\n\nВідновлення енергії продовжується поза котячою подобою.\n\nЗміна подоби звільняє заклинача від ефектів сповільнення руху.#increasing autoattack damage by {1}%#increasing movement speed by {2}%", "Невразливість до ефектів поліморфу. Збільшує силу атаки в залежності від показника спритності, збільшує шкоду автоатак на {1}% та збільшує швидкість руху на {2}%.[ Окрім того, все отримане зцілення збільшується на {1}%.#healing done to you is increased by {1}%]#increases autoattack damage by {1}%#increases movement speed by {2}%", en="Cat Form" },
[24858] = { "Подоба місячного совуха", "Набути[ астральної#Astral Form] подоби[ місячного совуха#Moonkin Form], збільшуючи шкоду від аркани та природи на {1}% та збільшуючи броню на {2}%.\n\nВ цій подобі ви збільшуєте швидкість заклять всіх учасників групи та рейду в межах {3} м на {4}%.\n\nЗміна подоби звільняє заклинача від ефектів обмеження руху.#deal by {1}%#armor by {2}%#within {3} yards by {4}%", "Шкоду заклять аркани та природи збільшено на {1}%.\nНевразливість до ефектів поліморфу.\nБроню збільшено на {2}%.#damage done increased by {1}%#Armor increased by {2}%", en="Moonkin Form" },
[783] = { "Мандрівна подоба", "Набути мандрівної подоби, збільшуючи швидкість руху на {1}%. Також захищає заклинача від ефектів поліморфу. Можна використовувати лише просто неба.\n\nЗміна подоби звільняє заклинача від ефектів обмеження руху.#speed by {1}%", "Невразливість до ефектів поліморфу. Швидкість руху збільшено на {1}%.#by {1}%", en="Travel Form" },

-- Glyph
[57855] = { "Символ причарування лісових звіряток", "Навчає вас здібності \"Причарування лісових звіряток\".", rune=127757, en="Glyph of Charm Woodland Creature" },
[59219] = { "Символ пориву", "Зменшує час відновлення здібності \"Порив\" на {1} с.#ability by {1} sec", en="Glyph of Dash" },
[94386] = { "Символ чарівного вогню", "Збільшує дальність дії здібності \"Чарівний вогонь\" на 10 м.", en="Glyph of Faerie Fire" },
[67598] = { "Символ безжалісного укусу", "\"Безжалісний укус\" зцілює вам {1}% від максимального запасу здоров'я за кожні {2} од. витраченої енергії.#for {1}% of#for each {2} Energy", en="Glyph of Ferocious Bite" },
[62080] = { "Символ зосередження", "Зменшує радіус \"Зорепаду\" на {1}%.#by {1}%", en="Glyph of Focus" },
[54810] = { "Символ шаленого відновлення", "Протягом {1} с після активації \"Шаленого відновлення\" ви отримуєте на {2}% більше зцілення. Натомість \"Шалене відновлення\" витрачає {3} люті та більше не перетворює лють на здоров'я.#For {1} sec#are {2}% more powerful#costs {3} Rage", en="Glyph of Frenzied Regeneration" },
[54831] = { "Символ урагану", "Ваші здібності \"Ураган\" та \"Астральна буря\" також сповільнюють рух цілей на {1}%.#by {1}%", en="Glyph of Hurricane" },
[54832] = { "Символ осяяння", "Коли ви застовсовуєте \"Осяяння\" не на себе, ви також отримуєте на {1}% слабший ефект.#at {1}% reduced", en="Glyph of Innervate" },
[54812] = { "Символ передвістя", "Поки ви не перебуваєте під ефектом затемнення, наступні здібності додають {1} сонячної або місячної енергії: \"Опутуюче коріння\", \"Циклон\", \"Чарівний вогонь\", \"Чарівний рій\", \"Масове обплетення\", \"Тайфун\", \"Дезорієнтуючий рев\", \"Вихор Урсола\" та \"Могутній стусан\".#grant {1} Solar", en="Glyph of Omens" },

