[110724] = { ref=63873 }, -- Leap of Faith
[110725] = { ref=73325 }, -- Leap of Faith
[110726] = { ref=73325 }, -- Leap of Faith
[113286] = { "Сонячний промінь", "Симбіоз: Ви викликаєте промінь сонячного світла на місце розташування ворожої цілі, який перериває вимову заклять та знемовлює всіх ворогів у області дії променя. Сонячний промінь існує {1} с.#for {1} sec", "Знемовлено.", en="Solar Beam" },
