[1130] = { "Мітка мисливця", "Відмічає ціль мисливською міткою, збільшуючи шкоду атак дальнього бою проти відміченої цілі на {1}%. Мисливець завжди бачить відмічену ціль та може її вистежувати.\n\n\"Арканічний постріл\", \"Постріл химери\", \"Команда \"Вбити\"\" та \"Вибуховий постріл\" автоматично накладають \"Мітку мисливця\" на ціль.\n\nТриває {2} хв.#target by {1}%#Lasts for {2} min", "Всі нападники завдають на {1}% більше шкоди в дальньому бою проти цієї цілі. Цю ціль видно під час непомітності чи невидимості.#gain {1}% increased", en="Hunter's Mark" },
-- Ice Trap
[13810] = { ref=13809 }, -- Ice Trap
-- Improved Serpent Sting
[82834] = { "Покращене жало змії", "\"Жало змії\" завдає на {1}% більше шкоди та завдає миттєвої шкоди в обсязі {2}% від сумарної шкоди періодичного ефекту.#by {1}%#equal to {2}% of its total", en="Improved Serpent Sting" },
-- Intimidation
[19577] = { "Залякування", "Наказати вашому вихованцю залякати ціль, приголомшуючи її на {1} с.#for {1} sec", "Приголомшить ціль на {1} с.#for {1} sec", en="Intimidation" },
[24394] = { nil, nil, "Приголомшено.", ref=19577 }, -- Intimidation
-- Invigoration
[53253] = { "Збадьорення", "Коли ваш вихованець завдає удару базовою атакою, ви з ймовірністю {1}% миттєво відновлюєте {2} од. концентрації.#a {1}% chance#regenerate {2} Focus", en="Invigoration" },
-- Kill Command
[34026] = { "Команда \"Вбити\"", "Віддати вихованцю команду \"вбити\", від чого той завдасть {1} шкоди та накладе на ціль \"Мітку мисливця\". \n\nВихованець має перебувати в межах {2} м від цілі.#inflict {1} damage#within {2} y", "Віддати вихованцю команду \"вбити\", від чого той завдасть {1} шкоди. \n\nВихованець має перебувати в межах {2} м від цілі.#inflict {1} damage#within {2} y", en="Kill Command" },
-- Kill Shot
[53351] = { "Вбивчий постріл", "Ви намагаєтеся добити поранену ціль точним пострілом, завдаючи їй {1}% шкоди зброї. Вбивчий постріл можна виконати лише проти ворогів, що мають не більше {2}% здоров'я.\n\nЯкщо \"Вбивчий постріл\" не вбиває ціль, відновлення миттєво завершується, але цей ефект може спрацьовувати лише раз на {3} с.#dealing {1}% weapon#have {2}% or less#every {3} sec", en="Kill Shot" },
-- Kindred Spirits
[56315] = { "Споріднені душі", "Збільшує максимальний запас концентрації для вас та вашого вихованця на {1}.#by {1}", en="Kindred Spirits" },
[88680] = { "Споріднені душі", en="Kindred Spirits" },
-- Lock and Load
[56343] = { "Захоплення цілі", "З ймовірністю {1}% після спрацьовування \"Заморожувальної пастки\" або \"Крижаної пастки\" та з ймовірністю {2}% після завдання шкоди \"Чорною стрілою\" наступні два \"Вибухові постріли\" не витрачатимуть концентрацію та не матимуть часу відновлення. Цей ефект триває {3} с та не може спрацьовувати частіше, ніж раз на {4} с.#have a {1}% chance#and a {2}% chance#for {3} sec#every {4} sec", en="Lock and Load" },
-- Master Marksman
[34487] = { "Майстер-стрілець", "З ймовірністю {1}% після \"Впевненого пострілу\" ви на {2} с отримаєте ефект \"Майстер-стрілець\". Після накопичення {3} ефектів час виконання та витрати концентрації наступного \"Прицільного пострілу\" зменшаться на {4}% протягом {5} с.#a {1}% chance#lasting {2} sec#reaching {3} stacks#by {4}% for {5} sec", en="Master Marksman" },
-- Mend Pet
[136] = { "Зцілення вихованця", "Відновлює вашому вихованцю {1}% здоров'я протягом {2} с.[При кожному ефекті зцілення, ви з ймовірністю {1}% знімете з вихованця 1 ефект прокляття, хвороби, магії чи отрути#has a {1}% chance to cleanse]#for {1}% of#over {2} sec", "Відновлює {1}% здоров'я вихованця кожні {2} с.#Heals {1}% of#every {2} sec", en="Mend Pet" },
-- Misdirection
[34477] = { "Перенаправлення", "Учасник групи чи рейду, на якого спрямована здібність, отримає загрозу, спричинену вашою наступною атакою і всіма діями, що відбудуться після неї протягом {1} с. Перенаправлена загроза не є постійною і зникає через {2} с.[ При застосуванні на вихованця відновлення здібності миттєво завершується.#Misdirection on your pet, the cooldown]#for {1} sec#after {2} sec", "Перенаправляє загрозу.", en="Misdirection" },
-- Rapid Fire
[3045] = { "Швидка стрілянина", "Збільшує швидкість дальнього бою на {1}% протягом {2} с.#ranged haste by {1}% for {2} sec", "Збільшує швидкість дальнього бою на {1}%.#ranged haste by {1}%", en="Rapid Fire" },
-- Rapid Recuperation
[53232] = { "Швидке відновлення", "Ви отримуєте {1} концентрації кожні {2} с поки перебуваєте під дією \"Швидкої стрілянини\".#gain {1} Focus every {2} sec", en="Rapid Recuperation" },
-- Scatter Shot
[19503] = { "Дезорієнтуючий постріл", "Постріл з короткої дистанції, що завдає {1}% шкоди від зброї[, знімає всі ефекти періодичної шкоди#removes all harmful damage over time effects] та дезорієнтує ціль на {2} с. Будь-яка шкода скасує ефект. Припиняє вашу атаку при використанні.#deals {1}%#for {2} sec", "Дезорієнтовано.", en="Scatter Shot" },
-- Serpent Spread
[87935] = { "Зміїний розмах", "Цілі, уражені вашим \"Багатозарядним пострілом\" також отримують ефект \"Жала змії\".", en="Serpent Spread" },
-- Silencing Shot
[34490] = { "Знемовлюючий постріл", "Постріл, що знемовлює ціль та перериває вимову заклять на {1} с.\n\nЗаміняє \"Зустрічний постріл\".#for {1} sec", "Знемовлено.", en="Silencing Shot" },
-- Snake Trap
[34600] = { "Зміїна пастка", "Розміститити пастку, яка випустить кілька отруйних змій, щоб атакувати першого ворога, який наблизиться до пастки. Змії[ отримують на {1}% менше шкоди від атак по області та#take {1}% reduced damage from area] помруть через {1} c. Пастка існуватиме {2} хв.#after {1} sec#for {2} min", en="Snake Trap" },
[82948] = { "Зміїна пастка", "Кинути у обране місце пастку, яка випустить кілька отруйних змій, щоб атакувати першого ворога, який наблизиться до пастки. Змії[ отримують на {1}% менше шкоди від атак по області та#take {1}% reduced damage from area of effect] помруть через {1} секунд. Пастка існуватиме {2} хв.#after {1} sec#for {2} min", en="Snake Trap" },
-- Steady Focus
[53220] = { "Впевнена концентрація", "Коли ви двічі поспіль виконуєте \"Впевнений постріл\", швидкість дальнього бою збільшується на {1}%, а \"Впевнений постріл\" відновлює на {2} більше концентрації протягом {3} с.#increased by {1}%#generate {2} additional#for {3} sec", "Швидкість дальнього бою збільшено на {1}%.\n\"Впевнений постріл\" відновлює на {2} більше концентрації.#speed increased by {1}%#generation increased by {2}", en="Steady Focus" },
[53224] = { "Впевнена концентрація", "Коли ви двічі поспіль виконуєте \"Впевнений постріл\", швидкість дальнього бою збільшується на {1}%, а \"Впевнений постріл\" відновлює на {2} більше концентрації протягом {3} с.#increased by {1}%#generate {2} additional#for {3} sec", en="Steady Focus" },
-- Steady Shot
[56641] = { "Впевнений постріл", "Впевнений постріл, що завдає {1}% шкоди зброї плюс {2}. Відновлює {3} од. концентрації.\n\nМожна використовувати в русі.#causes {1}% weapon damage plus {2}#Generates {3} Focus", en="Steady Shot" },
-- Trap Launcher
[77769] = { "Кидання пасток", "При активації ваші пастки можна кидати в обране місце в межах {1} м. Триває до скасування.#within {1} y", "При активації ваші пастки можна кидати в обране місце в межах {1} м.#within {1} y", en="Trap Launcher" },
-- Trap Mastery
[63458] = { "Опанування пасток", "Зменшує час відновлення пасток та \"Чорної стріли\" на {1} с та посилює пастки:\n\nКрижана та заморожувальна пастки - збільшує тривалість на {2}%.\n\nВибухова пастка та Чорна стріла - збільшує періодичну шкоду на {3}%.\n\nЗміїна пастка - збільшує кількість змій на {4}.#Black Arrow by {1} sec#Increases duration by {2}%#periodic damage done by {3}%#snakes summoned by {4}", en="Trap Mastery" },
-- Trueshot Aura
[19506] = { "Аура влучного пострілу", "Збільшує силу атаки ближнього та дальнього бою на {1}% всім учасникам групи та рейду в межах {2} м.#Grants {1}% increased#within {2} y", "Силу атаки ближнього та дальнього бою збільшено на {1}%.#by {1}%", en="Trueshot Aura" },
-- Widow Venom
[82654] = { "Отрута вдови", "Отруйний постріл, що зменшує ефективність будь-якого зцілення ворога протягом {1} с.#for {1} sec", "Отримане зцілення зменшено на {1}%.#by {1}%", en="Widow Venom" },
-- Wyvern Sting
[19386] = { "Жало виверни", "Жалючий постріл, що присипляє ціль на {1} с.  Будь-яка шкода скасовує ефект.  Лише один ефект жала від кожного мисливця може бути активним на одній цілі.#for {1} sec", "Сон.", en="Wyvern Sting" },

-- Glyph
[83495] = { "Символ відбиваючих клинків", "Коли вас атакують заклинанням під час дії \"Стримування\", ви з ймовірністю {1}% відіб'єте заклинання в нападника.#a {1}% chance", en="Glyph of Mirrored Blades" },

