[12654] = { "Займання", "Ваші удари закляттями \"Вогняна куля\", \"Стріла крижаного полум'я\", \"Полум'яний підрив\", \"Опік\" та \"Вогняна брила\" підпалюють ціль, завдаючи додатково {1}% шкоди протягом {2} с. Залишкова шкода попереднього ефекту додається при повторному накладанні.#additional {1}% over {2} sec", "Завдає {1} шкоди вогнем кожні {2} с.#Deals {1} Fire#every {2} sec", en="Ignite" },
[413841] = { "Займання", "Ваші критичні удари закляттями вогню змушують ціль горіти, завдаючи деякий відсоток від шкоди вашого закляття протягом {1} с.#over {1} sec", "Завдає шкоди вогнем кожні {1} с.#every {1} sec", en="Ignite" },
-- Improved Counterspell
[12598] = { "Покращене протизакляття", "\"Протизакляття\" також знемовлює ціль на {1} с.#for {1} sec", en="Improved Counterspell" },
-- Incanter's Ward
[1463] = { "Оберіг заклинателя", "Накладає на вас магічний оберіг тривалістю {2} с, що поглинає до {1} шкоди. Поглинена шкода відновлює до {3}% вашого максимального запасу мани.\n\nПісля закінчення ефекту ви отримуєте до {4}% збільшення шкоди заклинань протягом {5} с в залежності від поглинутої шкоди.\n\nПасивний ефект:\nЗбільшує шкоду заклинань на {6}% і регенерацію мани на {7}%. Цей ефект неактивний, поки \"Оберіг заклинателя\" відновлюється.#absorbing up to {1} damage for {2} sec#restore up to {3}% of#gain up to {4}% increased spell damage for {5} sec#spell damage by {6}%#regeneration by {7}%", "Поглинає до {1} шкоди, відновлюючи ману в залежності від поглинутої шкоди.#up to {1} damage", en="Incanter's Ward" },
-- Inferno Blast
[108853] = { "Полум'яний підрив", "Підриває ворога, завдаючи [{1}#for {1} Fire][від {1} до {2}#for {1} to {2} Fire] шкоди вогнем та гарантовано завдає критичної шкоди. При ударі поширює ефекти \"Вогняної брили\", \"Займання\" та \"Палання\" на {1} ворожі цілі в межах {2} м.\n\nЗаміняє \"Вогняний підрив\".#up to {1} nearby#within {2} yard", en="Inferno Blast" },
[118280] = { ref=108853 }, -- Inferno Blast
-- Invisibility
[66] = { "Невидимість", "Ви поступово зникаєте, зменшуючи свою загрозу щосекунди і стаєте невидимими через {1} с.[ Під час дії невидимості ви рухаєтесь на {1}% швидше.#move {1}% faster while invisible] \n\nЕфект скасовується при здійсненні будь-яких дій.#invisible over {1} sec", "Зникає.", en="Invisibility" },
-- Living Bomb
[44457] = { "Жива бомба", "Ціль стає живою бомбою і отримує {1} шкоди від вогню протягом {2} с. Після закінчення ефекту або смерті цілі станеться вибух, що завдасть {3} шкоди від вогню всім ворогам в межах {4} м. Вражає до {5} цілей. Це закляття має більшу ефективність при застосуванні на 1 чи 2 цілі.\n\nГлобальний час відновлення для цього закляття складає {6} с.#taking {1} Fire damage over {2} sec#additional {3} Fire#within {4} y#Limit {5} targets#has a {6} sec global", "{1} шкоди від вогню кожні {2} с. Через {3} с ціль вибухає, завдаючи {4} шкоди від вогню ворогам в межах {5} м.#Causes {1} Fire damage every {2} sec#After {3} sec#causing {4} Fire#within {5} y", en="Living Bomb" },
-- Mage Armor
[6117] = { "Магівський обладунок", "Збільшує майстерність на {1}.\n\nТривалість всіх негативних ефектів на вас зменшується на {2}%.#Mastery by {1}#reduced by {2}%", "Майстерність збільшено на {1}.\n\nТривалість всіх негативних ефектів зменшено на {2}%.#increased by {1}#reduced by {2}%", rune=119716, en="Mage Armor" },
