[30482] = { "Розплавлений обладунок", "Збільшує ймовірність критичного удару заклять на {1}%.\n\nЗменшує отриману фізичну шкоду на {2}%.#chance by {1}%#taken by {2}%", "Ймовірність критичного удару заклять збільшено на {1}%.\n\nОтриману фізичну шкоду зменшено на {2}%.#increased by {1}%#reduced by {2}%", rune=119716, en="Molten Armor" },
-- Polymorph
[118] = { "Поліморфія", "Перетворює ворога на [вівцю#sheep][пінгвіна#penguin][мавпу#monkey][дикобраза#porcupine][біле ведмежа#polar bear cub], змушуючи його блукати до {1} с.\n\n[Вівця не здатна#sheep][Пінгвін не здатен#penguin][Мавпа не здатна#monkey][Дикобраз не здатен#porcupine][Біле ведмежа не здатне#polar bear cub] атакувати та вимовляти закляття, але дуже швидко відновлює здоров'я. Будь-яка шкода поверне ціль до нормального стану.\n\nОдночасно лише одна ціль може бути під ефектом \"Поліморфії\".\nДіє лише на звірів, гуманоїдів і звіряток.#up to {1} sec", "Не може атакувати та вимовляти закляття. Прискорене відновлення здоров'я.", en="Polymorph" },
-- Presence of Mind
[12043] = { "Блиск розуму", "При активації ваше наступне закляття з тривалістю вимови менше {1} с буде миттєвим.\n\nНе впливає на глобальний час відновлення.#less than {1} sec", "Ваше наступне закляття з тривалістю вимови менше {1} с буде миттєвим.#less than {1} sec", en="Presence of Mind" },
-- Pyroblast
[11366] = { "Вогняна брила", "Кидає величезний вогняний валун, що завдає [{1}#causes {1} Fire][від {1} до {2}#causes {1} to {2} Fire] шкоди вогнем та додатково {1} шкоди вогнем протягом {2} с.\n\nПісля завдання прямої критичної шкоди вогнем двічі поспіль наступна \"Вогняна брила\" вимовиться миттєво, не витрачатиме мани та завдасть на {3}% більше шкоди.#additional {1} Fire damage over {2} sec#and deal {3}% additional damage", "{1} шкоди вогнем кожні {2} с.#{1} Fire damage every {2} sec", en="Pyroblast" },
-- Pyroblast!
[48108] = { "Вогняна брила!", "Коли ви двічі поспіль завдаєте критичного удару закляттями, ви зможете вимовити наступну \"Вогняну брилу\" миттєво та без витрат мани і вона завдасть на {1}% більше шкоди.#deal {1}%", "Наступне заклинання \"Вогняна брила\" завдає на {1}% більше шкоди і його можна вимовити миттєво та без витрат мани.#deals {1}%", en="Pyroblast!" },
