[475] = { "Зняття прокляття", "Знімає всі прокляття з дружньої цілі.[ При успішному знятті прокляття ваша шкода збільшується на {1}% протягом {2} секунд.#you deal {1}% additional damage for {2} sec]", en="Remove Curse" },
-- Ring of Frost
[82691] = { "Кільце криги", "Викликає кільце криги в обраному місці. Вороги, що потрапляють у кільце, заморожуються на {1} с. Триває {2} с. Радіус {3} м. Вражає до {4} цілей.#frozen for {1} sec#Lasts {2} sec#{3} yd radius#Limit {4} targets", "Заморожено.", en="Ring of Frost" },
