[2948] = { "Опік", "Обпікає ворога, завдаючи [{1}#for {1} Fire][від {1} до {2}#for {1} to {2} Fire] шкоди вогнем. Можна вимовляти в русі.", en="Scorch" },
-- Shatter
[12982] = { "Розтрощення", "Подвоює ймовірність завдання критичного удару всіма закляттями по замороженим цілям та збільшує її додатково на {1}%.#plus an additional {1}%", en="Shatter" },
-- Slow
[31589] = { "Сповільнення", "Зменшує швидкість руху цілі на {1}% та сповільнює вимову заклять на {2}% (на {3}% проти гравців). Триває {4} с. Одночасно може діяти лише на одну ціль.#speed by {1}%#casting time by {2}%#({3}% on player targets)#for {4} sec", "Швидкість руху зменшено на {1}%.\nВимову заклять сповільнено на {2}%.#speed reduced by {1}%#time increased by {2}%", en="Slow" },
