[86273] = { "Осяйне зцілення", "Ваші цілющі закляття захищають ціль щитом, що поглинає відсоток від обсягу зцілення. Щит триває {1} с, а обсяг поглинання збільшується від показника майстерності. Не спрацьовує від зцілення, спричиненого \"Маяком світла\".#lasts {1} sec", "Поглинає {1} шкоди.#Absorbs {1} damage.", en="Illuminated Healing" },
-- Infusion of Light
[53576] = { "Вливання Cвітла", "Критичні ефекти \"Священного шоку\" зменшують час вимови наступного \"Священного світла\", \"Божественного світла\" або \"Святого сяйва\" на {1} с.#by {1} sec", en="Infusion of Light" },
[54149] = { "Вливання Cвітла", "Критичні ефекти \"Священного шоку\" зменшують час вимови наступного \"Священного світла\", \"Божественного світла\" або \"Святого сяйва\" на {1} с.#by {1} sec", "Час вимови наступного \"Священного світла\", \"Божественного світла\" або \"Святого сяйва\" зменшено на {1} с.#by {1} sec", en="Infusion of Light" },
-- Inquisition
[84963] = { "Інквізиція", "Поглинає до {1} зарядів святої сили, щоб збільшити вашу шкоду святістю на {2}%, а ймовірність завдання критичного удару на {3}%. Триває {4} с за кожен поглинутий заряд святої сили.#up to {1} Holy#amage by {2}%#chance by {3}%#Lasts {4} sec", "Шкоду святістю збільшено на {1}%.\nЙмовірність завдання критичного удару збільшено на {2}%#done by {1}%#chance by {2}%", en="Inquisition" },
-- Item - Paladin Pvp Set Retribution 4P Bonus
[31829] = { "Предмет - Бонус 4 предметів PvP комплекту паладина розплати", "Ви отримуєте заряд святої сили при отриманні прямої шкоди. Цей ефект не може спрацьовувати частіше, ніж раз на {1} с.#every {1} sec", en="Item - Paladin Pvp Set Retribution 4P Bonus" },
-- Judgment
[20271] = { "Присуд", "Магічна атака, що вивільняє енергію печатки, завдаючи [{1}#cause {1} Holy][від {1} до {2}#cause {1} to {2} Holy] шкоди святістю[ та надаючи заряд святої сили#and generates one charge of Holy Power][, надаючи заряд святої сили та накладаючи ефект \"Фізичної вразливості\" на ціль.#and apply the Physical Vulnerability debuff to a target].", rune=81326, en="Judgment" },
-- Judgments of the Pure
[53657] = { "Присуд чистоти", "Ваш \"Присуд\" збільшує відновлення мани від духу під час бою.", "Відновлення мани від духу збільшено на {1}%.#by {1}%", en="Judgments of the Pure" },
[54151] = { "Присуд чистоти", "Ваш \"Присуд\" збільшує відновлення мани від духу під час бою на {1}% протягом {2} хв.#combat by {1}% for {2} min", en="Judgments of the Pure" },
-- Lay on Hands
[633] = { "Покладання рук", "Зцілює дружню ціль на значення, що дорівнює вашому максимальному запасу здоров'я[ та відновлює вам {1}% від максимального запасу мани#you to regain {1}% of your].\n\nНе можна використовувати на цілях зі \"Стриманістю\". Спричиняє ефект \"Стриманість\" на {1} хв.#for {1} min", en="Lay on Hands" },
-- Light of Dawn
[85222] = { "Світанкове сяйво", "Поглинає до {1} зарядів святої сили, створюючи перед собою хвилю цілющої енергії, яка зцілюює {2} учасникам групи чи рейду в межах {3} м, що мають найменше здоров'я, [{1}#for {1} per charge][від {1} до {2}#for {1} to {2} per charge] здоров'я за кожен заряд святої сили.#up to {1} Holy#up to {2} of the#within {3} y", en="Light of Dawn" },
-- Light's Hammer
[114158] = { "Молот світла", "Кинути в землю наповнений світлом молот, який протягом {1} с осяюватиме область радіусом {2} м дугою світла.\n\nДуга світла\nЗавдає ворогам {3} шкоди святістю і зменшує їхню швидкість руху на {4}% протягом {5} с. Зцілює союзникам {6} здоров'я кожні {7} с.#a {1} yard area with Arcing Light for {2} sec#Deals {3} Holy damage#speed by {4}% for {5} sec#Heals allies for {6} every {7} sec", en="Light's Hammer" },
[114918] = { "Молот світла", nil, "Зцілює {1} здоров'я та завдає {2} шкоди кожні {3} с.#Heals {1} and damages {2} every {3} sec", en="Light's Hammer" },
[122773] = { "Молот світла", en="Light's Hammer" },
[135517] = { "Молот світла", "Кинути в землю наповнений світлом молот, який протягом {1} с осяюватиме область радіусом {2} м дугою світла.\n\nДуга світла\nЗавдає ворогам {3} шкоди святістю та зцілює союзникам {4} здоров'я кожні {5} с.#a {1} yard area with Arcing Light for {2} sec#Deals {3} Holy damage#and {4} healing#area every {5} sec", en="Light's Hammer" },
