[26023] = { "Гонитва за правосуддям", "Ваша швидкість руху збільшується на {1}% та додатково на {2}% за кожен заряд святої сили (до {3} зарядів).#gain {1}% movement#additional {2}%#up to {3}", en="Pursuit of Justice" },
-- Reckoning
[62124] = { "Воздаяння", "Провокує ціль атакувати вас, та збільшує загрозу, яку ви спричиняєте цілі, на {1}% протягом {2} с.#target by {1}% for {2} sec", "Спровоковано.", en="Reckoning" },
