[53551] = { "Священне очищення", "Ваше закляття \"Очищення\" тепер також знімає з цілі магічні ефекти.", en="Sacred Cleansing" },
-- Sacred Shield
[20925] = { "Священний щит", "Захищає ціль щитом Світла на {1} с. Щит поглинає до {2} шкоди кожні {3} с.\n\nОдночасно може діяти лише на одну ціль.#for {1} sec#absorbs up to {2} damage every {3} sec", "Поглинає до {1} шкоди кожні {2} с.#up to {1} damage every {2} sec", en="Sacred Shield" },
-- Sanctified Wrath
[53376] = { "Благодатний гнів", "\"Гнів помсти\" триває на {1}% довше та дозволяє використовувати деякі здібності частіше.\n\nСвятість\nЗменшує час відновлення \"Священного шоку\" на {2}% та збільшує ймовірність критичного ефекту \"Священного шоку\" на {3}%.\n\nЗахист\n\"Присуд\" відновлюється на {3}% швидше та надає додатковий заряд святої сили.\n\nРозплата\nЗменшує час відновлення \"Молота гніву\" на {4}%.#cooldown of Holy Shock by {1}%#critical strike chance of Holy Shock by {2}%#cooldown of Judgment by {3}%#cooldown of Hammer of Wrath by {4}%", en="Sanctified Wrath" },
-- Sanctity of Battle
[25956] = { "Святість битви", "Показник швидкості також зменшує час відновлення та глобальний час відновлення здібностей[ \"Священний шок\",#Holy Shock] \"Присуд\", \"Удар лицаря\"[, \"Молот праведника\"#Hammer of the Righteous][, \"Освячення\", \"Священний гнів\", \"Щит месника\", \"Щит праведника\"#Consecration, Holy Wrath, Avenger's Shield, Shield of the Righteous][, \"Екзорцизм\"#Exorcism] та \"Молот гніву\".", en="Sanctity of Battle" },
-- Seal of Insight
[20165] = { "Печатка прозріння", "Наповнює вас святим світлом, збільшуючи швидкість[ атак ближнього бою та#your melee and] вимови заклять на {1}%, посилюючи цілющі закляття на {2}% та надаючи атакам ближнього бою шанс зцілити [вам {1} здоров'я#heal you for {1}][учаснику групи чи рейду з найменшим запасом здоров'я {1} здоров'я#most wounded member of your party or raid for {1}][ та відновити {1}% мани поза аренами та полями бою#restore {1}% of base mana when striking a target outside of an Arena or Battleground].#speed by {1}%#healing spells by {2}%", "Швидкість вимови заклять збільшено на {1}%.\nЕфективність зцілення збільшено на {2}%.\nАтаки ближнього бою можуть зцілити вас[ та відновити ману при ураженні цілі, що не є гравцем].#speed by {1}%#healing by {2}%", en="Seal of Insight" },
-- Seal of Justice
[20164] = { "Печатка правосуддя", "Наповнює вас святим світлом, внаслідок чого атаки ближнього бою завдають додатково {1}% шкоди святістю та зменшують швидкість руху цілі на {2}% протягом {3} с.#deal {1}%#by {2}% for {3} sec", "Атаки ближнього бою завдають додаткової шкоди святістю та зменшують швидкість руху на {1}%.#by {1}%", en="Seal of Justice" },
[20170] = { nil, nil, "Зменшує швидкість руху на {1}%.#by {1}%", ref=20164 }, -- Seal of Justice
-- Seal of Righteousness
[20154] = { "Печатка праведності", "Наповнює вас святим світлом, внаслідок чого атаки ближнього бою завдають {1}% шкоди зброї всім цілям в межах {2} м#deal {1}% weapon#within {2} y", "Атаки ближнього бою завдають шкоди святістю всім цілям в межах {1} м.#within {1} y", en="Seal of Righteousness" },
-- Seal of Truth
[31801] = { "Печатка правди", "Наповнює вас святим світлом, внаслідок чого ваші атаки ближнього бою завдають додатково {1}% шкоди зброї у вигляді шкоди від святості та накладають \"Осуд\" на ціль.\n\nЗамінює \"Печатку наказу\".#deal {1}%", "Атаки ближнього бою завдають шкоди святістю протягом {1} с.#over {1} sec", rune=31803, en="Seal of Truth" },
-- Selfless Healer
[85804] = { "Самовідданий цілитель", "Успішні \"Присуди\"[ створюють заряд святої сили та#generate a charge of Holy Power and] зменшують час вимови та витрати мани наступного \"Зблиску світла\"[, \"Божественного світла\" або \"Святого сяйва\"#Divine Light, or Holy Radiance] на {1}% за кожен ефект та збільшують ефективність при зціленні інших цілей на {2}% за кожен ефект. Ефект накопичується до {3} разів.#by {1}% per stack and#by {2}% per stack when#Stacks up to {3} times", en="Selfless Healer" },
-- Shield of the Righteous
[53600] = { "Щит праведника", "Вдаряє ціль щитом, завдаючи {1} шкоди святістю, зменшуючи отриману фізичну шкоду на {2}% протягом {3} с та спричиняючи ефект \"Бастіон слави\".#causing {1} Holy#take by {2}% for {3} sec", rune=114637, en="Shield of the Righteous" },
