[85256] = { "Вердикт храмовника", "Потужний удар зброєю, що поглинає {1} заряди святої сили, завдаючи {2}% шкоди зброї плюс {3}.#consumes {1} charge#deal {2}% weapon damage plus {3}", en="Templar's Verdict" },
