[10326] = { "Вигнання зла", "Сила Світла змушує[ гуманоїда,#Humanoid][ звіра,#Beast] невмерлого, потвору чи демона втікати протягом {1} с. Одночасно можна вигнати лише одну ціль.#for up to {1} sec", "Змушений втікати.", en="Turn Evil" },
