[84839] = { "Помста", "Щоразу, коли ви отримуєте шкоду, ви збільшуєте силу атаки на {1}% від отриманої шкоди (без врахування ефектів зменшення шкоди) протягом {2} с.#gain {1}% of#for {2} sec", en="Vengeance" },
