[1230944] = { "Дудка сера Дорнела", "Гра на дудці викликає випадкового звіра на допомогу, що збільшує ваші фізичні здібності на 30 с.  Можна використовувати лише під час бою.", "Гра на дудці викликала звіра.", en="Sir Dornel's Didgeridoo" },
[1230980] = { "Штандарт Штільца", "Поставити штандарт Штільца, що збільшує запас здоров'я навколишніх союзників на {1} протягом {2} с.#nearby allies by {1} for {2} sec", "Піідбадьорено штандартом.", en="Stiltz's Standard" },
[1231091] = { "Прототип нітроприскорювачів", "Засипте в пристрій довільну кількість пороху і натисніть на кнопку.\n\nВаша швидкість і ймовірність серйозного поранення чи смерті зростатимуть залежно від кількості доданого пороху.\n\nМожна використовувати тільки в Новому Авалоні.", "Швидкість руху збільшено на {1}-{2}%.#by {1} to {2}%", en="Prototype Nitro Boosts" },
[1231254] = { "Багряний оплот", "Збільшує навичку захисту на {1}, а броню на {2} протягом {3} с.#Defense by {1} and#Armor by {2} for {3} sec", "Навичку захисту збільшено на {1}\nБроню збільшено на {2}.#Defense increased by {1}#Armor increased by {2}", en="Scarlet Bulwark" },
[1231258] = { "Гній", "При атаці в ближньому бою ви можете уразити ворога гниллю, що завдає {1} шкоди від природи кожні {2} с усім ворогам в межах {3} м навколо нього протягом {4} с. Швидкість атаки цілей також зменшується на {5}%.#dealing {1} Nature damage every {2} sec#an {3} yard radius of the caster for {4} sec#slowed by {5}%", en="Rot" },
[1231267] = { "Готовність", "Звичайна дальня атака по цілі готує вас до бою, збільшуючи вашу навичку захисту на {1}, а швидкість атаки ближнього бою - на {2}% протягом {3} секунд.#Defense by {1} and melee attack speed by {2}% for {3} sec", en="En Garde!" },
[1231289] = { "Аватар гори", "Прикликати волю гори, що збільшує фізичну шкоду на {1}%, броню на {2} і розмір на {3}% протягом {4} секунд.#dealt by {1}%, armor by {2}, and size by {3}% for {4} sec", "Збільшує фізичну шкоду на {1}%, броню на {2} і розмір на {3}%.#dealt by {1}%, armor by {2}, and size by {3}%", en="Avatar of the Mountain" },
