[1231605] = { "ЕКСПЕРИМЕНТ-800М!!!", "Перетворитись на гармату Багряного Походу на {1} c, внаслідок чого ваші атаки дальнього бою спричиняють вибухи, що завдають від {2} до {3} шкоди вогнем усім ворогам у радіусі {4} м від вашої цілі.#Cannon for {1} sec#explode for {2} to {3} Fire#within {4} yards of", "БУМ!", en="EXPERIMENT-8OOM!!!" },
[1231612] = { "Снодійний удар", "Ваша наступна звичайна атака ближнього бою, виконана протягом 20 c, завдасть арканічної шкоди в обсязі 200% від вашої сили заклять і приспить ціль на 20 c.", "Наступна звичайна атака ближнього бою завдасть арканічної шкоди в обсязі 200% від вашої сили заклять і приспить ціль на 20 c.", en="Soporific Strike" },
[1231620] = { "Гибель Тіра", "Завдання періодичної шкоди з ймовірністю {1}% збільшить шкоду та зцілення ваших заклять на {2} протягом {3} с.#a {1}% chance to grant {2} spell#healing for {3} sec", en="Tyr's Fall" },
[1231624] = { "Реліквії червоних драконів", "Завдання неперіодичної шкоди вогнем з ймовірністю {1}% збільшить вашу шкоду від вогню на {2}% протягом {3} с.#has a {1}% chance#damage dealt by {2}% for {3} sec", en="Remnants of the Red" },
[1231656] = { "Головоломка", "Дальність дії заклять \"Муки розуму\", \"Вогняний підрив\" і \"Висмоктування життя\" збільшується на 10 м, але \"Муки розуму\" більше не сповільнюють ворогів.", en="Conundrum" },
[1231681] = { "Шпиль жертовності", "Поки ви маєте більше 30% здоров'я, ефективність вашого зцілення збільшується на 200, але при цьому ви жертвуєте 5% запасу здоров'я від кожної вимови заклять. Під дією цього ефекту ви можете отримувати тільки пряме неперіодичне зцілення. (Ефект можна перемикати)", "Жертва заради спільного блага.\nВи можете отримувати зцілення тільки пряме неперіодичне зцілення.", en="Spire of Sacrifice" },
[1231694] = { "Вирок", "Бойові закляття виносять ворогам вирок на 20 с. Щоразу, коли вони завдаватимуть шкоди, їхні цілі зцілюватимуться на 3% від максимального запасу здоров'я. Триває 20 сек або до 10 ударів. (Відновлення - 60 секунд)", en="Condemnation" },
[1231698] = { "Вірність", "Якщо в межах 20 м навколо вас немає ворогів, ви будете зцілювати всім учасникам групи в межах 20 м 125 здоров'я кожні 3 c. Група може одночасно отримувати це зцілення лише від одного гравця.", en="Fealty" },
