[1219952] = { "Душа закликача комет", "Наповнити наплічники душею закликача комет:\n\nЗменшує час вимови та глобальний час відновлення \"Зоряного вогню\" на {1} с.#global cooldown of Starfire by {1} sec", en="Soul of the Cometcaller" },
[1219931] = { "Душа Сноходця", "Наповнити наплічники душею Сноходця:\n\nКритичні ефекти ваших цілющих заклять спричиняють ефект \"Входження в сон\", продовжуючи відновлення мани під час вимови заклять зі швидкістю {1}% від звичайної протягом {2} секунд.#you {1}% of your mana regeneration while casting for {2} sec", en="Soul of the Dreamwalker" },
[1219943] = { "Душа знекровлювача", "Наповнити наплічники душею знекровлювача:\n\nКритичні удари здібностей \"Нівечення\", \"Шматування\" та \"Безжалісний укус\" спричиняють у цілі кровотечу, що завдає {1}% від завданої шкоди протягом {2} с.#Bleed for {1}% of the damage done over the next {2} sec", en="Soul of the Exsanguinator" },
[1219933] = { "Душа пернатого мудреця", "Наповнити наплічники душею пернатого мудреця:\n\nЗбільшує зцілення \"Дикого росту\" на {1}%. Окрім того, \"Дикий ріст\" тепер можна використовувати в подобі місячного совуха, а його зцілення в цій подобі збільшено на {2}%.#healing from Wild Growth by {1}%#additional {2}% in that form", en="Soul of the Feathered Sage" },
