-- Starfall
[429820] = { "Зорепад", "Завдає від {1} до {2} шкоди арканою ворогам у вибраній області кожні {3} с протягом {4} с.#{1} to {2}#every {3} sec#for {4} sec" }, -- Starfall
[439748] = { "Зорепад", "Ви викликаєте шквал зірок з неба протягом {1} с, що вражають цілі в межах {2} м від вас, завдаючи від {3} до {4} арканічної шкоди та {5} арканічної шкоди іншим ворогам в межах {6} м від цілі. Викликає до {7} зірок.\n\nЗміна подоби та верхова їзда скасовує ефект. Будь-який ефект, що призводить до втрати контролю над персонажем, призупиняє ефект зорепаду.\n\nВзаємодіє з більшістю талантів і ефектів, що й \"Місячний вогонь\".#over {1} sec#targets within {2} y#dealing {3} to {4} Arcane#and {5} Arcane#enemies within {6} y#Maximum {7} stars", "Викликає зорі з неба." }, -- Starfall
[439768] = { "Зорепад", "Отримати здібність \"Зорепад\":", rune=439748 }, -- Starfall
-- Starsurge
[417157] = { "Зоряний сплеск", "Сплеск зоряної енергії, що завдає від {1} до {2} шкоди арканою та збільшує шкоду від наступного \"Зоряного вогню\" на {3}% протягом {4} секунд.\n\n\"Зоряний сплеск\" взаємодіє з усіма ефектами, що й \"Гнів\" та \"Зоряний вогонь\".#{1} to {2}#cast by {3}% for {4} sec", "Ваш наступний \"Зоряний вогонь\" завдасть на {1}% більше шкоди.#deals {1}% increased" }, -- Starsurge
[424715] = { "Зоряний сплеск", "Отримати здібність \"Зоряний сплеск\":", rune=417157 }, -- Starsurge
-- Sunfire
[414684] = { "Сонячний вогонь", "Обпікає ворога, миттєво завдаючи від {1} до {2} шкоди від природи, а потім додатково {3} шкоди від природи протягом {4} с.#for {1} to {2}#additional {3} Nature damage over {4} sec", "{1} шкоди від природи кожні {2} с.#{1} Nature damage every {2} sec" }, -- Sunfire
[414687] = { "Сонячний вогонь (ведмідь)", "Обпікає ворога, миттєво завдаючи від {1} до {2} шкоди від природи, а потім додатково {3} шкоди від природи протягом {4} с.#for {1} to {2}#additional {3} Nature damage over {4} sec", "{1} шкоди від природи кожні {2} с.#{1} Nature damage every {2} sec" }, -- Sunfire (Bear)
[414689] = { "Сонячний вогонь (кіт)", "Обпікає ворога, миттєво завдаючи від {1} до {2} шкоди від природи, а потім додатково {3} шкоди від природи протягом {4} с. Збільшує довжину комбінації на 1 прийом.#for {1} to {2}#additional {3} Nature damage over {4} sec", "{1} шкоди від природи кожні {2} с.#{1} Nature damage every {2} sec" }, -- Sunfire (Cat)
[414692] = { "Сонячний вогонь", "Отримати здібність \"Сонячний вогонь\" і замінити \"Місячний вогонь\" на \"Сонячний вогонь\" (ведмідь) або \"Сонячний вогонь\" (кіт) в подобі ведмедя, кота та жахливого ведмедя. Це закляття взаємодіє з усіма ефектами, що й \"Місячний вогонь\".", rune={414684, 414687, 414689} }, -- Sunfire
[414696] = { "Сонячний вогонь" }, -- Sunfire
[414700] = { "Сонячний вогонь" }, -- Sunfire
-- Survival Instincts
[408024] = { "Інстинкти виживання", "Після активації ви отримуєте {1}% до максимального запасу здоров'я, а об'єм вашого неффізичного зцілення збільшується на {2}% протягом {3} секунд. Після закінчення ефекту здоров'я втрачається. Можна використовувати в будь-якій подобі.\n\nКрім того, коли ви ухиляєтесь від атаки, ви отримуєте {4} люті у ведмежій подобі, {5} енергії у котячій подобі та {6}% від максимального запасу мани у будь-якій іншій подобі.#grants you {1}% of your#deal by {2}% for {3} sec#{4} rage#{5} energy#or {6}% of your", "Запас здоров'я збільшено на {1}%.#by {1}% of" }, -- Survival Instincts
