[459600] = { "Зменшення затримки вимови Арканічного спалаху", "З ймовірністю {1}% ви уникнете затримки вимови закляття \"Арканічний спалах\" при отриманні шкоди.#a {1}% chance to avoid", en="Arcane Blast Cast Pushback Reduction" },
[468512] = { "Крижана могутність", "Збільшує ймовірність критичного удару заклять \"Крижана стріла\", \"Чарокрижана стріла\" та \"Крижана куля\" на {1}% та збільшує критичну шкоду заклять \"Крижана стріла\", \"Чарокрижана стріла\" та \"Крижана куля\" на {2}% протягом {3} с.#Orb spells by {1}%, and#spells by {2}% for {3} sec", "Ймовірність критичного удару заклять \"Крижана стріла\", \"Чарокрижана стріла\" та \"Крижана куля\" збільшено на {1}%.\nКритичну шкоду заклять \"Крижана стріла\", \"Чарокрижана стріла\" та \"Крижана куля\" збільшено на {2}%.#chance increased by {1}%#damage increased by {2}%", en="Frost Potency" },
[467141] = { "Прискорення думок", "Прискорює думки, збільшуючи швидкість вимови заклять на {1}% протягом {2} с.#spells by {1}% for {2} sec", "Швидкість вимови заклять збільшено на {1}%.#by {1}%", en="Mind Quickening" },

