[1219856] = { "Душа вогняного злиття", "Наповнити наплічники душею вогняного злиття:\n\n\"Вогняна брила\" завдає на {1}% більше шкоди цілі, що перебуває під ефектом періодиної шкоди вашої \"Вогняної кулі\".#Pyroblast deals {1}% increased damage", en="Soul of Fiery Convergence" },
[1219844] = { "Душа часової жаги", "Наповнити наплічники душею часової жаги:\n\nЩоразу, коли ви зцілюєте ціль \"Регенерацією\", час відновлення \"Повернення у часі\" скорочується на {1} с.#Rewind Time is reduced by {1} sec", en="Soul of Temporal Longing" },
[1219850] = { "Душа зимової хватки", "Наповнити наплічники душею зимової хватки:\n\nЗбільшує ймовірність спрацювання руни \"Крижані пальці\" на {1}%.#Frost rune by an additional {1}%", en="Soul of Winter's Grasp" },
[1219846] = { "Душа арканіста", "Наповнити наплічники душею передчуття:\n\n\"Арканічний спалах\" має на {1}% більшу ймовірність викликати ефект \"Обстрілу снарядами\", а \"Обстріл снарядами\" впливає на \"Регенерацію\" так само, як і на \"Арканічні снаряди\".#Blast gains a {1}% additional chance", en="Soul of the Arcanist" },
[1219843] = { "Душа хроноцілителя", "Наповнити наплічники душею хроноцілителя:\n\nЗбільшує все ваше хрономантичне зцілення на {1}%.#healing you deal by {1}%", en="Soul of the Chronohealer" },
[1219851] = { "Душа кріоманта", "Наповнити наплічники душею кріоманта:\n\nЗбільшує шкоду заклять \"Крижана стріла\" та \"Чарокрижана стріла\" на {1}%.#Spellfrost Bolt spells by {1}%", en="Soul of the Cryomancer" },
[1219853] = { "Душа повелителя стихій", "Наповнити наплічники душею повелителя стихій:\n\nВаша шкода збільшується на {1}% протягом {2} с після вимови закляття нової школи магії.#gain {1}% increased damage for {2} sec each time you", en="Soul of the Elementalist" },
[1219847] = { "Душа вічного хранителя", "Наповнити наплічники душею вічного хранителя:\n\nВаші \"Темпоральні маяки\", спричинені \"Масовою регенерацією\" тепер тривають {1} с.#Mass Regeneration now last {1} sec", en="Soul of the Eternal Caretaker" },
[1219852] = { "Душа пробудника", "Наповнити наплічники душею пробудника:\n\nВи невразливі до будь-якої шкоди, поки промовляєте \"Прикликання\".", en="Soul of the Evoker" },
[1219859] = { "Душа займання", "Наповнити наплічники душею займання:\n\nЗбільшує шкоду від таланту \"Займання\" на {1}%.#your Ignite talent by {1}%", en="Soul of the Igniter" },
[1219855] = { "Душа розпалювача", "Наповнити наплічники душею розпалювача:\n\nЗменшує загрозу ваших вогняних заклять на {1}%.#by your Fire spells by {1}%", en="Soul of the Kindler" },
[1219854] = { "Душа магічних обладунків", "Наповнити наплічники душею магічних обладунків:\n\n\"Магівський обладунок\" збільшує відновлення мани під час вимови заклять ще на {1}%. \"Розплавлений обладунок\" збільшує шкоду і зцілення ваших заклять на {2}. \"Льодяний обладунок\" збільшує ймовірність спрацювання ефекту \"Крижаних пальців\" на {3}%.#regeneration while casting by an additional {1}%#Armor increases your spell damage and healing by {2}#Ice Armor grants {3}% increased chance", en="Soul of the Magical Armorer" },
[1219857] = { "Душа вічного полум'я", "Наповнити наплічники душею вічного полум'я:\n\nЗбільшує загальну шкоду ефекту \"Вогняної кулі\" на {1}% від первинної шкоди.#equal to {1}% of its impact damage", en="Soul of the Perpetual Blaze" },
[1219845] = { "Душа передчуття", "Наповнити наплічники душею передчуття:\n\nВаші \"Арканічні снаряди\" відновлюють вам {1}% від базових витрат мани на них щоразу, коли завдають шкоди.#Missiles refunds {1}% of its base", en="Soul of the Precognitive" },
[1219858] = { "Душа піромана", "Наповнити наплічники душею піромана:\n\n\"Вогняний підрив\" також збільшує ймовірність критичного удару вашого наступного вогняного закляття на {1}% протягом {2} с.#gain {1}% increased critical strike chance for {2} sec", en="Soul of the Pyromaniac" },
[1219842] = { "Душа резонатора", "Наповнити наплічники душею резонатора:\n\nВаші \"Темпоральні маяки\" тривають на {1}% довше.#Beacons last {1}% longer", en="Soul of the Reverberant" },
[1219848] = { "Душа заклинача", "Наповнити наплічники душею заклинача:\n\n\"Арканічний спалах\" додатково збільшує шкоду та зцілення на {1}% за кожен ефект.#done by an additional {1}% per stack", en="Soul of the Spellbider" },
[1219860] = { "Душа палія", "Наповнити наплічники душею палія:\n\nЗакляття \"Вогняна куля\", \"Стріла крижаного полум'я\" та \"Стріла руйнівного полум'я\" завдають на {1}% більше шкоди за кожен ваш вогняний ефект на цілі, максимум до {2}%.#spells gain {1}% increased damage for each#up to a maximum increased of {2}%", en="Soul of the Torcher" },
[1219849] = { "Душа творця оберегів", "Наповнити наплічники душею творця оберегів:\n\n\"Щит мани\", \"Вогняний оберіг\" та \"Крижаний оберіг\" поглинають на {1}% більше шкоди та поміщають на ціль \"Темпоральний маяк\" на {2} с.#Ward absorb {1}% more damage#Beacon on the target for {2} sec", en="Soul of the Wardshaper" },

