[467525] = { "Егіда збереження", "Зцілює цілі {1}% від максимального запасу здоров'я та збільшує її максимальний запас здоров'я на {2}% протягом {3} с.#target for {1}% of#health by {2}% for {3} sec", "Максимальний запас здоров'я збільшено на {1}%.#by {1}%", en="Aegis of Preservation" },
[469191] = { "Фоліант Кассандри", "Прочитати пророцтво з фоліанту, щоб збільшити шанс критичного удару наступного неперіодичного заклинання чи атаки на {1}%.#granting {1}% increased", "Шанс критичного удару наступного заклинання чи атаки збільшено на {1}%.#{1}% increased", en="Cassandra's Tome" },
[459604] = { ref=23043 }, -- Mind Blast Cast Pushback Reduction
[468531] = { "Швидке зцілення", "Зменшує час вимови закляття \"Велике зцілення\" на {1}% та витрати мани на закляття зцілення на {2}%, а застосування \"Сповіді\" на союзників діє на {3} додаткові цілі протягом {4} с.#Heal spells by {1}%#healing spells by {2}%#target up to {3} additional targets for {4} sec", "Час вимови закляття \"Велике зцілення\" зменшено на {1}%.\nВитрати мани на закляття зцілення зменшено на {2}%.\nЗастосування \"Сповіді\" на союзників діє на {3} додаткові цілі.#decreased by {1}%#reduced by {2}%#up to {3} additional", en="Rapid Healing" },
[459603] = { "Зменшення тривалості Знесиленої душі", "Зменшує тривалість ефекту \"Знесилена душа\" на {1} с.#Soul by {1} sec", en="Weakened Soul Duration Decrease" },

