[1219831] = { "Душа архієпископа", "Наповнити наплічники душею архієпископа:\n\n\"Коло зцілення\" відновлює найбільш пораненому учаснику групи цілі на {1}% більше здоров'я.#member of the target party for {1}% more", en="Soul of the Archbishop" },
[1219826] = { "Душа обрядовості", "Наповнити наплічники душею обрядовості:\n\nЗбільшує ймовірність критичного ефекту \"Молитви зцілення\" та \"Кола зцілення\" на {1}%.#Circle of Healing by {1}%", en="Soul of the Celebrant" },
[1219840] = { "Душа споглядача", "Наповнити наплічники душею споглядача:\n\n\"Пронизування розуму\" тепер вимовляється миттево, завдає на {1}% більше шкоди і його можна вимовити під час промовляння інших заклять.#instant, deals {1}% more damage", en="Soul of the Contemnor" },
[1219836] = { "Душа смертоносця", "Наповнити наплічники душею смертоносця:\n\nЗменшує час відновлення закляття \"Слово тіні: Смерть\" на {1} с.#Death spell by {1} sec", en="Soul of the Deathdealer" },
[1219830] = { "Душа відданого", "Наповнити наплічники душею відданого:\n\nВаша \"Молитва загоєння\" отримує ще {1} заряди.#Mending gains {1} additional", en="Soul of the Devotee" },
[1219827] = { "Душа вірності", "Наповнити наплічники душею вірності:\n\nДозволяє {1}% відновлення мани продовжуватися під час вимови заклять.#{1}% of your Mana", en="Soul of the Faithful" },
[1219824] = { "Душа квапливого цілителя", "Наповнити наплічники душею квапливого цілителя:\n\nЗменшує час вимови \"Швидкого зцілення\" на {1}, а час вимови \"Великого зцілення\" на {2}.#-{1} sec to the casting time of Flash Heal and -{2} sec to", en="Soul of the Hastened Healer" },
[1219835] = { "Душа руйнівника розуму", "Наповнити наплічники душею руйнівника розуму:\n\nКритичні удари \"Вибуху розуму\" зменшують час промовляння наступного закляття \"Муки розуму\" на {1}% та збільшують його загальну шкоду на {2}%.#Flay by {1}% while increasing its total damage by {2}%", en="Soul of the Mind Breaker" },
[1219832] = { "Душа каяття", "Наповнити наплічники душею каяття:\n\nЗменшує час відновлення \"Сповіді\" на {1} с.#Penance spell by {1} sec", en="Soul of the Penitent" },
[1219841] = { "Душа розносника чуми", "Наповнити наплічники душею розносника чуми:\n\nРуна \"Біль і страждання\" також може поновлювати тривалість ефекту \"Ненажерної чуми\".", en="Soul of the Plaguebringer" },
[1219825] = { "Душа довершеності", "Наповнити наплічники душею довершеності:\n\nЗбільшує шанс завдання критичного удару всіма заклинаннями та атаками на {1}%.#your critical strike chance with spells and attacks by {1}%", en="Soul of the Refined" },
[1219829] = { "Душа резонансу", "Наповнити наплічники душею резонансу:\n\n\"Коло зцілення\" та \"Сповідь\" також накладають на цілі ефект періодичного зцілення, що зцілює {1}% від обсягу зцілення протягом {2} с.#that heals for {1}% as much over {2} sec", en="Soul of the Resonant" },
[1219828] = { "Душа проникливості", "Наповнити наплічники душею проникливості:\n\nВаше періодичне зцілення з ймовірністю {1}% дозволить миттєво вимовити наступне закляття з тривалістю вимови менше {2} с.#periodic healing has a {1}% chance#casting time less than {2} sec", en="Soul of the Serendipitous" },
[1219833] = { "Душа хранителя душ", "Наповнити наплічники душею хранителя душ:\n\nЗбільшує обсяг поглинання закляття \"Слово сили: Щит\" на {1}%.#Shield spell by {1}%", en="Soul of the Soul Warder" },
[1219837] = { "Душа духовного джерела", "Наповнити наплічники душею духовного джерела:\n\n\"Слово тіні: Біль\" з ймовірністю {1}% за кожен ранг таланту \"Витік душі\" спричиняє його ефект при завданні шкоди, або з ймовірністю {2}% за ранг коли ціль під дією ефекту \"Слово тіні: Біль\" помирає. \"Внутрішня зосередженість\" також з ймовірністю {3}% за ранг спричиняє ефект \"Витоку душі\".#Pain has a {1}% chance per talent#or a {2}% chance per talent point#Focus also has a {3}% chance per talent", en="Soul of the Spirit Font" },
