[1219799] = { "Душа абіссала", "Наповнити наплічники душею абіссала:\n\nПоки активна \"Метаморфоза\", ваші бойові здібності та виклик демонів не потребують уламків душі. Крім того, ви зцілюєтеся на {1}% від максимального запасу здоров'я коли завдаєте шкоди за \"Тіньовим опіком\".#you heal for {1}% of your maximum health", en="Soul of the Abyssal" },
[1219792] = { "Душа палія", "Наповнити наплічники душею палія:\n\nЩоразу, коли ви вражаєте ціль \"Розпаленням\", ваша шкода від вогню збільшується на {1}% протягом {2} с, ефект накопичується до {3} разів.#gain {1}% increased Fire damage for {2} sec#stacking up to {3} times", en="Soul of the Arsonist" },
[1219791] = { "Душа провісника хаосу", "Наповнити наплічники душею провісника хаосу:\n\nЗменшує час відновлення \"Стріли Хаосу\" на {1}% та збільшує шкоду \"Стріли Хаосу\" та \"Тіньової стріли\" на {2}%. Окрім того, \"Стріла Хаосу\" тепер може спричинити ефект таланту \"Покращена тіньова стріла\" та збільшує максимальну кількість його ефектів на {3} (не поєднується з додатковими ефектами від \"Тіньового полум'я\").#cooldown on your Chaos Bolt by {1}%#Shadow Bolt damage done by {2}%#causes it to have {3} additional charges", en="Soul of the Chaos Harbinger" },
[1219787] = { "Душа нищівника", "Наповнити наплічники душею нищівника:\n\nЙмовірність спрацювання таланту \"Сутінки\" збільшується на {1}%. \"Спалення\" з ймовірністю {2}% спричиняє ефект \"Винищення\" незалежно від запасу здоров'я цілі.#talent has a {1}% increased chance#Incinerate has a {2}% chance", en="Soul of the Decimator" },
[1219795] = { "Душа демонічного екзорциста", "Наповнити наплічники душею демонічного екзорциста:\n\n\"Вигнання\" тепер вимовляється миттєво і його можна застосувати до себе, поки ви є демоном. Самовигнання неможливе, якщо ви маєте ефект стриманості, а його застосування накладає на вас ефект стриманості на 1 хвилину.", en="Soul of the Demonic Exorcist" },
[1219794] = { "Душа повелителя демонів", "Наповнити наплічники душею пекельного пастиря:\n\nПосилює ефект таланту \"Майстер-демонолог\" на {1}%.#Demonologist talent by {1}%", en="Soul of the Demonlord" },
[1219797] = { "Душа полум'яного привида", "Наповнити наплічники душею полум'яного привида:\n\nУдари \"Тіньового розсічення\" з ймовірністю {1}% дають вам уламок душі, завершують відновлення \"Випалення душі\" та протягом {2} с дозволяють вимовити наступне \"Випалення душі\" миттєво.#hits have a {1}% chance to#Soul Fire within {2} sec", en="Soul of the Flamewraith" },
[1219798] = { "Душа плотожера", "Наповнити наплічники душею плотожера:\n\nПоки ви націлені на ворога в межах {1} м, «Витік життя» відновлює вам ману ціною здоров'я вашої цілі, але завдає на {2}% менше шкоди. Кількість отриманої мани залишається незмінною.#enemy within {1} yards#health but deals {2}% reduced damage", en="Soul of the Fleshfeaster" },
[1219793] = { "Душа пекельного пастиря", "Наповнити наплічники душею пекельного пастиря:\n\nЗбільшує силу атаки та шкоду заклять, які ваш прислужник отримує від ваших атрибутів, на {1}%.#from your attributes by {1}%", en="Soul of the Infernal Shepherd" },
[1219789] = { "Душа злостивця", "Наповнити наплічники душею злостивця:\n\nПеріодична шкода заклять \"Тіньове полум'я\", \"Нестабільна химородь\" та \"Прокляття агонії\", а також шкода вартового скверни з ймовірністю {1}% спричинить ефект \"Тінетрансу\".#Felguard have a {1}% chance to grant", en="Soul of the Malevolent" },
[1219803] = { "Душа вісника болю", "Наповнити наплічники душею вісника болю:\n\nПротягом {1} с після використання \"Тіньового розсічення\" ваш \"Палючий біль\" вражає {2} додаткову ціль в межах ближнього бою.#For {1} sec after using#Pain strikes {2} additional", en="Soul of the Pain Spreader" },
[1219796] = { "Душа стражденного", "Наповнити наплічники душею стражденного:\n\nЩоразу, коли ви отримуєте шкоду, ви і ваш вихованець отримуєте ману в обсязі, що дорівнює отриманій шкоді, але не більше {1} мани за раз. Цей ефект може спрацьовувати лише раз на кілька секунд. #to a maximum of {1} mana per", en="Soul of the Pained" },
[1219786] = { "Душа довершеності", "Наповнити наплічники душею довершеності:\n\nЗбільшує шанс завдання критичного удару всіма заклинаннями та атаками на {1}%.#your critical strike chance with spells and attacks by {1}%", en="Soul of the Refined" },
[1219802] = { "Душа ритуаліста", "Наповнити наплічники душею ритуаліста:\n\nЕфект \"Демонічної жертви\" зберігається, доки ви не викличете принесеного в жертву прислужника. Одночасно на вас може діяти лише один ефект \"Демонічної жертви\".", en="Soul of the Ritualist" },
[1219788] = { "Душа гноїтеля", "Наповнити наплічники душею гноїтеля:\n\nЗбільшує шкоду ваших заклять періодичної шкоди та вартового скверни на {1}%.#Felguard pet by {1}%", en="Soul of the Rotbringer" },
