-- Judgement of Justice
[20184] = { "Присуд правосуддя", "Вивільнення енергії цієї печатки засудить ворога на {1} с, не дозволяючи йому втекти. Одночасно може бути активним лише один присуд від кожного паладина.#enemy for {1} sec", "Не може втекти, а швидкість руху не можу бути більшою за звичайну. Не вважається ефектом обмеження руху.", en="Judgement of Justice" },
-- Judgement of Light
[20185] = { "Присуд світла", "Наповнює паладина божественним світлом на {1} хв, надаючи кожній атаці ближнього бою ймовірність зцілити паладину {2} здоров'я. Лише одна печатка може бути задіяна на паладинові одночасно.\n\nВивільнення енергії цієї печатки засудить ворога на {3} с, надаючи атакам ближнього бою проти засудженої цілі ймовірність зцілити нападнику {4}% здоров'я. Одночасно може бути активним лише один присуд від кожного паладина.#light for {1} min#heal the Paladin for {2}#enemy for {3} sec#attacker for {4}%", "Атаки проти цієї цілі можуть зцілити нападників.", en="Judgement of Light" },
[20267] = { "Присуд світла", "Вивільнення енергії печатки засудить ворога на {1} с, надаючи атакам ближнього бою проти засудженої цілі ймовірність відновити нападнику {2}% здоров'я, а також спричиняючи додатковий ефект активної печатки. Одночасно може бути активним лише один присуд від кожного паладина.#for {1} sec#for {2}% of", en="Judgement of Light" },
