[348704] = { "Печатка осквернення", "Наповнює паладина святою силою, внаслідок чого атаки ближнього бою, накладають ефект осквернення крові, що завдає додатково {1} шкоди святістю протягом {2} с. Осквернення крові може накопичуватись до {3} разів. Атаки паладина також завдаватимуть додатково до {4}% шкоди від зброї у вигляді шкоди святістю в залежності від кількості ефектів. Лише одна печатка може бути задіяна на паладинові одночасно. Триває {5} хв.\n\nВивільнення енергії цієї печатки завдасть {6} шкоди святістю, що збільшується на {7}% за кожен ефект осквернення крові на цілі.#deals {1} additional Holy damage over {2} sec#up to {3} times#deals up to {4}% weapon#Lasts {5} min#will deal {6} Holy#increased by {7}% for each", "Атаки ближнього бою завдають додаткової шкоди святістю протягом {1} с.#over {1} sec", en="Seal of Corruption" },
-- Seal of Justice
[20164] = { "Печатка правосуддя", "Наповнює паладина духом правосуддя на {1} хв, надаючи кожній атаці ближнього бою ймовірність приголомшити ціль на {2} с. Лише одна печатка може бути задіяна на паладинові одночасно.\n\nВивільнення енергії цієї печатки завдасть ворогу {3} шкоди святістю#justice for {1} min#stun for {2} sec#deal {3} Holy", "Атаки ближнього бою можуть приголомшити ціль на {1} с.#for {1} sec", en="Seal of Justice" },
-- Seal of Light
[20165] = { "Печатка світла", "Наповнює паладина божественним світлом на {1} хв, надаючи кожній атаці ближнього бою ймовірність зцілити паладину {2} здоров'я. Лише одна печатка може бути задіяна на паладинові одночасно.\n\nВивільнення енергії цієї печатки завдасть ворогу {3} шкоди святістю.#light for {1} min#heal the Paladin for {2}#will deal {3} Holy", "Атаки ближнього бою можуть відновити вам здоров'я.", en="Seal of Light" },
-- Seal of Righteousness
[20154] = { "Печатка праведності", "Наповнює паладина святим духом на {1} хв, надаючи кожній атаці ближнього бою {2} додаткової шкоди святістю. Лише одна печатка може бути задіяна на паладинові одночасно.#for {1} min#attack {2} additional Holy", "Атаки ближнього бою завдають {1} додаткової шкоди святістю.#additional {1} Holy", en="Seal of Righteousness" },
[21084] = { "Печатка праведності", "Наповнює паладина святим духом на {1} хв, надаючи кожній атаці ближнього бою {2} додаткової шкоди святістю. Лише одна печатка може бути задіяна на паладинові одночасно.\n\nВивільнення енергії печатки завдасть {3} шкоди ворогу.#for {1} min#attack {2} additional Holy#cause {3} Holy", "Атаки ближнього бою завдають {1} додаткової шкоди святістю.#additional {1} Holy", en="Seal of Righteousness" },
-- Seal of Vengeance
[31801] = { "Печатка помсти", "Наповнює паладина святою силою, внаслідок чого атаки ближнього бою накладають ефект \"Священної помсти\", що завдає додатково {1} шкоди святістю протягом {2} с. \"Священна помста\" може накопичуватись до {3} разів. Кожна атака завдає до {4}% шкоди зброї у вигляді шкоди святістю, в залежності від кількості накладених ефектів. Лише одна печатка може бути задіяна на паладинові одночасно. Триває {5} хв.\n\nВивільнення енергії цієї печатки завдасть {6} шкоди святістю, збільшеної на {7}% за кожен ефект \"Священної помсти\" на цілі.#deals {1} additional Holy damage over {2} sec#stack up to {3} times#deals up to {4}% weapon#Lasts {5} min#will deal {6} Holy damage#increased by {7}% for each", "Атаки ближнього бою завдають шкоди святістю протягом {1} с.#over {1} sec", en="Seal of Vengeance" },
-- Seal of Wisdom
[20166] = { "Печатка мудрості", "Наповнює паладина божественною мудрістю на {1} хв, надаючи кожній атаці ближнього бою ймовірність відновити паладину {2}% мани. Лише одна печатка може бути задіяна на паладинові одночасно.\n\nВивільнення енергії цієї печатки завдасть ворогу {3} шкоди святістю.#wisdom for {1} min#restore {2}% of the paladin#will deal {3} Holy", "Атаки ближнього бою можуть відновити ману.", en="Seal of Wisdom" },
-- Seals of the Pure
[20224] = { "Печатки чистоти", "Збільшує шкоду печаток праведності, помсти та осквернення, а також їх ефектів присуду на {1}%.#by {1}%", en="Seals of the Pure" },
